}

#contentCurve
{
   background-color:#E8E8E8
}

.uiBoxLightblue
{
  background-color:#F0E3D3;
  border:1px solid #F0E3D3;
}

#pagelet_main_column_personal
.uiSideNav .selectedItem .item, .uiSideNav .selectedItem .item:hover, .uiSideNav ul .selectedItem .subitem, .uiSideNav ul .selectedItem .subitem:hover
{
  background-color:#F0E3D3
}

.uiBlingBox
{
  border-bottom:0px !important;
}

#pagelet_ego_pane, .fbTimelineWideRightCol .pagesTimelineButtonPagelet, .fbTimelineScrubber 
{
  display:none;
}

#u_0_5p
{
  background: rgba(255, 255, 255, 0.0);
}

#pageFooter
{
  background: rgba(255, 255, 255, 0.6);
}

.navigationGrid, .fcg
{
  margin-left: 20px !important;
}

.UFIRow, .UFIReplyList, .shareRedesignText, .shareRedesignContainer, .shareRedesign .shareMediaVideo
{
