css:
@-moz-document domain("theoldreader.com") {

/*Remove favicon, adjust alignment, correct counters, use Google fonts etc.
Author: Marina Mazzoni

ffd3e0 - rosino chiaro
d2efff - azzurrino chiaro
15d71f - verde forte
ff2a60 - geranio forte
ff5939 - arancio forte
fe3c33 - rosso forte
50c2ff - azzurro
168eff - blu
e6e5eb - grigino chiaro
8c8b91 - grigio scuro
*/
@font-face {
  font-family: 'Source Sans Pro';
  font-style: normal;
  font-weight: 400;
  src: url(http://themes.googleusercontent.com/static/fonts/sourcesanspro/v5/ODelI1aHBYDBqgeIAH2zlBM0YzuT7MdOe03otPbuUS0.woff) format('woff');
}

/* inizio */

body{
background-color:#d2efff !important;
border-width: 0px !important;
padding: 0px 0px 0px 0px !important;
font-family: Source Sans Pro, sans-serif !important;
font-size: medium !important;
opacity:0.8 !important;
text-shadow: none !important;
}

div.well.sidebar-nav{
background-color: #e6e5eb !important;
padding-top: 25px !important;
}

div.slide{
background-color: transparent !important;
border-width: 0px !important;
padding: 0px 0px 0px 0px !important;

}

#subscribe a, span{
background-image:  none !important;
background-color: transparent !important;
color: #fe3c33 !important;
font-size: small !important;
border-width: 0px !important;
padding: 0px 0px 0px 0px !important;
}

.bg {
display: none !important;
}

.icon {
display: none !important;
}

.btn-primary {
box-shadow: 0px 0px 0px !important;
padding: 0px 0px 0px 0px !important;
border-style: none !important;
width: 100% !important;
display: table !important;
}

div [class="span3 subscribe-fixed-top"]{
height: auto !important;
}

.btn-large {
border-radius: 0px 0px 0px 0px !important;
border-style: none !important;
font-size: medium !important;
}

/*badge stile contatore ios7*/
.badge {
color: #fff !important;
background-color: #fe3c33 !important;
border-radius: 6px 6px 6px 6px !important;
border-radius: 10px !important;
padding: 2px !important;
}

.slider{
background-color: #fe3c33 !important;
opacity: 0.7 !important;
}

.label-feed{
background-color: #50c2ff !important;
}

.label-social{
background-color: #50c2ff !important;
}

.sidebar-nav li{
line-height: normal !important;
text-transform:uppercase !important;
}

.floating {
background-color: #d2efff !important;
}

.page-header{
color: #8c8b91 !important;
size: medium !important;
border: none !important;
}

.page-header a{
color: #168eff !important;
size: medium !important;
text-shadow: none !important;
}
.post.unread {
border-left: 4px solid #ff5939 !important;
}

.content-body {
opacity: 0.9 !important;
}
i.icon.icon-list-alt, i.icon.icon-arrow-up, i.icon-chevron-down, i.icon-chevron-up, i.icon.icon-refresh, i.icon.icon-check, a.btn.btn-small{
display: inline !important;
color: #168eff !important;
}
.btn-small {
padding: 4px !important;
font-size: small !important;
background-image: none !important;
}

ul.nav.pull-right a{
size: medium !important;
color: #168eff !important;
}
div.navbar-inner{
background-color: #e6e5eb !important;
}

/* no icone */
i{
background-image:none !important;
}

i.icon-chevron-down{
background-image:none !important;
}

i.icon-chevron-right{
background-image:none !important;
}
}
