css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

#profilebox {
   display: none !important;
}

td.content {
   font-family: lucida sans unicode, lucida grande, sans-serif !important;
   font-size: 14px !important;
   line-height: 21px !important;
}

textarea#status {
   font-family: lucida console, monospace !important;
   color: forestgreen !important;
}

a:link, a:visited {
   padding: 2px !important;
}

a:hover {
   padding: 2px !important;
   color: #000 !important;
   background-color: #ffd100 !important;
   border-bottom: 2px solid #000 !important;
   text-decoration: none !important;
}

.tabMenu { text-align: center !important; margin: 25px 0 0 !important;}
.tabMenu li { display: inline !important; margin-left: -5px !important;}
.tabMenu li a { display: inline !important; padding: 3px 20px 1px 20px !important; background: #e6e6e6 !important; text-decoration: none !important; color: #4c4c4c !important; border-top: 1px solid #cecece !important; border-right: 1px solid #cecece !important; border-left: 1px solid #cecece !important; }
.tabMenu li a:hover { text-decoration: none !important; color: black !important; background: #ffd100 !important; border-bottom: 0 !important;}
.tabMenu li.active a { background: #fff !important; color: #000 !important; border-bottom: 1px solid #fff !important; }

#settingsNav {text-align: center; margin: 10px 0 0 0;}
#settingsNav li { display: inline; margin-left: -5px;}
#settingsNav li a { display: inline !important; padding: 3px 20px 1px 20px !important; background: #e6e6e6 !important; text-decoration: none !important; color: #4c4c4c !important; border-top: 1px solid #cecece !important; border-right: 1px solid #cecece !important; border-left: 1px solid #cecece !important; }
#settingsNav li a:hover { text-decoration: none !important; color: black !important; background: #ffd100 !important; border-bottom: 0 !important;}
#settingsNav li.active { background: #fff !important; color: #000 !important; border-bottom: 1px solid #fff !important; }


.wrapper {
   border: 1px solid #dcdcdc !important;
}

#navigation ul li {
   background-color: #fff !important;
   border-left: none !important;
}

#navigation ul li a:link {
   background-color: #fff !important;
   border-left: none !important;
}

#navigation ul li a:hover {
   background-color: #ffd100 !important;
   border-left: none !important;
}

}
