@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.mangareader.net") {
    #imgholder img {
        visibility: visible !important;        
        position: absolute !important;
        top: 0 !important;
        height: 100% !important;
        width: auto !important;
        left:0 !important;
        right:0 !important;
        margin-left:auto !important;
        margin-right:auto !important;
    }
    #wrapper_header {
        display: none !important; 
    }
    #adtop {
        display: none !important; 
    }
    #topchapter {
        display: none !important; 
    }    
    #bottomchapter {
        display: none !important; 
    }
    #clear {
        display: none !important; 
    }    
    #wrapper_footer {
        display: none !important; 
    }
    #adchapterlink {
        display: none !important; 
    }
    #zoomer {
        display: none !important; 
    }
    #adchapterleft {
        display: none !important;        
    }
    #adchapterright {
        display: none !important; 
    }    
    #adfooter {
        display: none !important; 
    }
}
