css:
@-moz-document domain("feedly.com") {
*{font-family: "Microsoft Yahei",arial,sans-serif !important;text-rendering:optimizeSpeed !important;}

/*change small pic to bigger*/
div[id*="_main_visual"] {background-size:contain !important;background-repeat:no-repeat;}

/*floatingBar*/
#floatingBar {padding-top:0px !important;padding-bottom:0px !important;}
/*floatingTitleBar*/
#floatingTitleBar {padding-top:0px !important;padding-bottom:0px !important;}

/*item context text*/
.info{padding-top:0px !important;padding-bottom:0px !important;}
/*item context text > title*/
a[id*="_main_title"] {margin-top:0px !important;margin-bottom:0px !important;}

/*whole item*/
.u5Entry {height:270px !important; margin-bottom:10px !important;}

/*auto hide "hide/save"*/
div[id*="_main_wikiBar"] {position:relative !important;top:-32px !important;display:none!important;}
.u5Entry:hover > div > div > div[id*="_main_wikiBar"] {display:block!important;}

/*left sidebar*/
.feedIndex {height:22px !important;}

/*item window*/
#floatingEntry .inlineFrame {padding:20px !important;}
#floatingEntry {top:4px !important;}
.websiteCallForAction {display:none !important;}
.topWikiWidget {margin-top:12px !important;padding-bottom:12px !important;}
}
