css:
@-moz-document url("http://www.cnbeta.com/"), url-prefix("http://www.cnbeta.com/articles") {
.tiny_bar, .global_navi, .main_content_right{display: none;}
.wrapper > section {display: none;}
.wrapper .main_content {display: inline;}
.cb_box + section {display:none;}
.cb_box + section + section + section {display: none;}
.main_content_left .blue_bar {display: none;}
.main_content + section {display: none;}
.copyright {display: none;}



.global_head {padding-top: 30px;}


.cb_box {padding: 0 !important; background:none; border:none;}
.main_content_left {width: 980px;}
.slide {width: 310px;}
.slide dd {width: 300px; height: 25px;}
.imgbox240x310 img {width: 310px; max-width: none; max-height: 325px;}
.slide_nav {top: 300px;}
.slide_area {height: 370px;}
.headline {padding:0 2px 0 0;width: 620px;}
.tab_item {background: none;}

.section, article {margin-bottom: 15px;}
.white_bottom {background-repeat: repeat;}


.focus {width: 300px;}
.focus_area {width: 300px;}
.imgbox247x135 img {max-height: 188px; max-width: 300px; width:300px;}
.realtime {width: 640px;}
.content_body, .realtime h3, .realtime_list{background: none;}


.blue_bar, .blue_bar_v2, .white_bottom, .white_bottom_v2, .black_bar, .black_bottom, .yellow_bar, .yellow_bottom_v1, .yellow_bottom_v2, .read_more .more {background-repeat: repeat;}


.all_news_list dl {width: 390px; padding: 20px 0; margin: 0 0 0 90px;}
.all_news_list dd p {margin-right: 100px;}

.read_more {margin-bottom: 50px;}


.headline .top_text, .focus dd p, .realtime_list, .all_news_list dd p {color:#777777;}
}

@-moz-document url-prefix("http://www.cnbeta.com/articles") {
.introduction {display: none;}
#googleAd_afc {display: none;}
.clearfix + section {display: none;}
.commt_r {display: none;}
.content_body {display: none;}

.wrapper {width: 75%; max-width: 940px; padding: 1.2em 0 1.2em 46px; padding: 0;}
.global_head, .main_content_left {width: 75%; margin: 0 auto;}


/*FONT*/
.article .body header h2 {font-size: 2em;}
.article .body .content p {font-size: 1.2em; line-height: 1.55em; margin: 1.2em 0; color: #080808}
}
