css:
@-moz-document url("https://www.tumblr.com/login"), url("https://www.tumblr.com/"), url("https://www.tumblr.com/register"), url("http://www.tumblr.com/new/blog"), url("http://www.tumblr.com/inbox"), url("http://www.tumblr.com/help"), url("https://www.tumblr.com/preferences"), url("https://www.tumblr.com/lookup"), url("http://www.tumblr.com/explore"), url("http://www.tumblr.com/following"), url("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/new/"), url-prefix("http://www.tumblr.com/reblog/"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/tumblelog/"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/spotlight/"), url-prefix("http://www.tumblr.com/edit/") , url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {

Body {
  	background: url('http://media.tumblr.com/34075df02f5ed78c72a0423955ec7c79/tumblr_inline_ml2icfQOTu1qz4rgp.gif') right #fff repeat fixed !important;
	color:#2a2a2a!important;
    font-family: trebuchet ms;
	font-size:12px !important;
   	background-size: 25%   !important;
  }


body, a, a:hover {
	cursor: url('http://downloads.totallyfreecursors.com/thumbnails/music.gif'), progress !important;
 }


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background: url('http://static.tumblr.com/vv7anc1/9gNmnpud5/transc.png') transparent repeat;
  }


a:link, a:active, a:visited {
  	color: #04204c !important; 
  	text-decoration: none !important;
	-webkit-transition: all 0.3s linear;
	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
    font-family: trebuchet ms;
  } 


a:hover {
  	color: #e4e4e4 !important;
  	text-decoration: none !important;
	-webkit-transition: all 0.3s linear;
	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
    font-family: trebuchet ms;
  } 
    

.post_info a:link, .post_info a:active, .post_info a:visited {
  	color: #04204c !important;
	-webkit-transition: all 0.3s linear;
  	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
    text-decoration: none !important;
    font-family: trebuchet ms;
  } 
    
.post_info a:hover {
  	color: #e4e4e4 !important; 
    text-decoration: none !important;
	-webkit-transition: all 0.3s linear;
    -moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
    font-family: trebuchet ms;
  } 


.notification  {
  	background: #930019 !important; border: none !important; 
	color: #fff !important; font-size: 11px !important;
  }


.notification :link, .notification :active, .notification :visited {
  	color: #c1c1c1 !important;
    text-decoration: none !important;
    border: none !important;
  }


h1 {
  	color:white !important;
  }


div.l-header .tab.iconic a {
  	background-color: #92EBE2 !important;
  	border-radius: 50px !important;  
	-webkit-transition: all 0.3s linear;
	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
  }


div.l-header #home_button a {
  	background-color: #930019;
  	background-position: 0 0!important;
  	border-radius: 50px !important;  
	-webkit-transition: all 0.3s linear;
	-moz-transition: all 0.3s linear;
	transition: all 0.3s linear;
  }


.avatar_frame {
  	border: 0px solid #fff !important;
    background-color:#FFF !important;
    border-radius: 50px;
  }


.post_avatar {
	border: 2px solid #fff !important;
    box-shadow: 0px 0px 7px #fff !important;
  }


.post_avatar .post_avatar_image, .post_avatar .post_sub_avatar_image {
	display: block;
	opacity: 0;
}


.search_query {
	display: block;
	position: absolute;
	top: 0;
	right: 0;
	bottom: 0;
	left: 0;
	height: 100%;
	border-width: 0;
	background: transparent;
	color: #04204c;
	font-weight: bold;
	-moz-user-select: text;
	-webkit-font-smoothing: antialiased;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
  }


div.l-header .selected .selection_nipple {
display: none;
}
}
