@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikihow.com") {

body, div#main, div#aside, div#main div#featured_article, table#Actions_table td , div#article p, #Authors, div#Authors h2, #featured_article div#SpotlightHead, #header p#logo span{background: #253960 url(http://farm4.static.flickr.com/3124/2628948238_12d8ff6b92_o.png) top left repeat-x  !important; color:#fff !important;}

div#article p {background:#253960 !important;}

a {color:#DACA7D !important;}

#article_tabs p.tabON, #article_tabs p.tabOFF {background: #FFF !important;
	border: 1px solid #999 !important;
	}
	
	
#article_tabs p.tabON a, #article_tabs p.tabOFF a {background: #DDD !important;
	border: 0 !important;
	color: #000 !important;
	display: block !important;
	font-size: 11px !important;
}

#article_tabs p.tabON a:hover, #article_tabs p.tabOFF a:hover {
	background: #222 !important;
	color: #CC9900 !important;}

#article_tabs p.tabOFF {border-left: 0 !important;}




}
