css:
@namespace xul url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document url("about:"), url("about:logo"), url("chrome://global/content/about.xhtml") {
  body:after {
    content: url("data:image/png;base64,");
    position: absolute;
  }
}

@-moz-document url("about:"), url("chrome://global/content/about.xhtml") {
  body:after {
    top: 75px;
    left: 205px;
  }
  #version { margin-top: -18px !important; }
  a {
    color: #039 !important;
    text-decoration: none !important;
    text-shadow: 0 0 0 #039;
  }
  a:not(#vendorURL):hover { text-decoration: underline !important; }
}

@-moz-document url("about:logo") {
  body:after {
    top: -moz-calc(50% - 118px + 40px);
    top:      calc(50% - 118px + 40px);
    left: -moz-calc(50% - 150px + 175px);
    left:     calc(50% - 150px + 175px);
  }
}

@-moz-document url("chrome://browser/content/aboutDialog.xul") {
  xul|*#leftBox, xul|*#PMleftBox {
    background-image: url("data:image/png;base64,") !important;
    background-position: 66px 82px !important;
  }
}

@-moz-document url("chrome://browser/content/browser.xul") {
  xul|tab[label="About:"] xul|image.tab-icon-image {
    list-style-image: url("chrome://branding/content/icon16.png") !important;
  }
}
