css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnbeta.com") {
/*首页调整*/
.realtime {
width: 100% !important;
float: left !important;
padding:0px !important;
}
.focus,
.main_content_left > section:nth-child(1){
display:none !important}
.main_content_left {margin-top:-6px !important;width: 67% !important;}
.read_more .more{background:none !important}
.main_content_right{ margin:0 0 0 10px !important;float:left !important;}
.global_navi .main_navi, .cb_prev, .cb_next, .cb_more, .dot_box li, .dot_box li.current ,.blue_bar,.blue_bar_v2{background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAeCAYAAAAGos/EAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAUSURBVChTY9BqXPUfhEcZjav+AwBfdYuxbCap2wAAAABJRU5ErkJggg==) !important}
.all_news_list dl {
float: none !important;
display: inline !important;
}
.all_news_list dd p {
width:85% !important;
float:left !important;
margin-left:100px !important;
padding:0px !important;
}
.items_area{margin-left:3px !important;}
dl dt a {font-size:16px !important;}
/*正文调整*/
#googleAd_afc{display:none !important;}
.introduction{
width:100% !important;
padding:0 0 0 10px !important;
margin:0 !important}
.introduction > div:nth-child(1) > a:nth-child(1) > img:nth-child(1){margin:0 20px 0px 20px !important;}
/*评论区调整*/
.rate_num{display:none !important;}
.post_commt .commt_l,.post_commt textarea{width:99% !important;padding-left:5px !important}
.textarea_wraper{width:99% !important}
.main_content_left > section:last-child{display:block !important}
}
