css:
/**
 * @title Yandex Lenta Dark
 * @description
 *   Dark theme for Yandex Lenta RSS reader.
 *   Background pattern taken from SubtlePatterns.com
 * @version 1.3
 * @history
 * v1.3
 * - Styled: feed carousel
 * v1.2
 * - Styled: feed chooser
 * v1.1
 * - Styled: feed settings form
 * - Styled: new feed popup
 * v1.0
 * - Initial release
 */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://mail.yandex.ru/my/#lenta/"),
               url-prefix("https://mail.yandex.by/my/#lenta/"),
               url-prefix("https://mail.yandex.ua/my/#lenta/"),
               url-prefix("https://mail.yandex.kz/my/#lenta/"){
    body {
        color: #999 !important;
    }
    a:link, a:visited {
        color: #ccc !important;
    }
    a:hover, a:active {
        color: #fea !important;
    }
    
    .b-theme-teaser {
        display: none !important;
    }
	
	.b-stamp {
		background-image: none !important;
	}
	
    .b-page__content,
    .b-layout__inner,
    .b-lenta-messages,
    .b-lenta-message {
        background-color: #404040 !important;
    }
	.b-page__content {
		background-image: url("data:image/png;base64,");
	}
    .b-lenta-message_open {
        border-top: 1px solid #606060 !important;
    }
    .b-lenta-message-head__content {
        color: #ccc !important;
    }
    .b-lenta-messages_collapsed .b-lenta-message-head__content:hover,
    .b-lenta-messages_collapsed .b-lenta-message-head__content:hover .b-lenta-message-head__source{
	   color: #fea !important
    }
    .b-lenta-messages_collapsed .b-lenta-message-head__content:hover {
        background-color: #333 !important;
        transition: background-color 0.5s;
    }
    .b-lenta-messages_feed .b-lenta-message_open .b-lenta-message-head__subject {
        font-size: 100% !important;
    }
    .b-lenta-message_open .b-lenta-message-head__content__i {
        margin-bottom: 1em;
    }
    .b-messages-head__action {
        color: #909090 !important;
    }
    .b-pager, 
    .b-footer,
    .b-pseudo-link,
    .b-lenta-message-foot__actions .b-pseudo-link {
        color: inherit !important
    }
	/* feed settings */
	.b-mail-tabs_lenta-settings .b-mail-tabs__content {
		background: #333 !important;
	}
    /* toolbar */
    .b-service-tabs {
        border-bottom: none !important;
    }
    .b-service-tabs__item {}
    .b-service-tabs__item_current {
        background: #494949 !important;
    }
    .b-service-tabs__item_current {
        bottom: -1px !important;
        border-top: 1px solid #606060 !important;
        border-left: 1px solid #606060 !important;
        border-right: 1px solid #606060 !important;
    }
    a.b-service-tabs__item:hover {
        border-top: none !important;
        background: #606060 !important;
        transition: background-color 0.5s;
    }
    a.b-service-tabs__item_current:hover {
        border-top: 1px solid #606060 !important;
    }
	a.b-toolbar__item_selected {
		background: #404040 !important;
	}
    .b-toolbar,
    .b-toolbar__i,
    .b-toolbar__block,
    .b-toolbar__block_chevron {
        background-color: #494949 !important;
    }
    .b-toolbar {
        border-top: 1px solid #606060 !important;
    }
    .b-toolbar__block_chevron {
        border-left: 1px solid #606060 !important;
    }
    a.b-toolbar__item {
        color: #909090 !important;
    }
    a.b-toolbar__item:hover {
        background: #404040 !important;
    }
    
    .b-layout__inner {
        border-left: 1px solid #606060 !important;
        border-bottom: 1px solid #606060 !important;
    }
    /* sidebar */
    .b-layout__left {
        background-color: #494949 !important;
        border: 1px solid #606060 !important;
        border-left: none !important;
    }
    .b-layout__left .b-teaser {
        background-color: #444 !important;
        border-bottom: 1px solid #606060 !important;
    }
    .b-folders__setup {
        border-bottom-color: #606060 !important;
    }
    .b-folders__nesting__handle__icon {
        border: 1px solid #999 !important;
        background-color: #606060 !important;
        border-radius: 8px !important;
    }
    .b-folders__nesting__handle__icon:hover {
        border: 1px solid #ccc !important;
    }
    .b-folders__folder_current {
        background: none !important;
    }
    .b-folders__folder_current .b-folders__folder__name,
    .b-folders__folder_current .b-folders__folder__info {
        background: #333 !important;
    }
    .b-label__count {
        color: #909090 !important;
    }
    .b-sidebar-nav_sticky .b-sidebar-nav__underlay {
        background: #333 !important;
    }
    /* dropdowns & popups */
    .b-mail-dropdown__box,
	.b-popup__box__content {
        background: #333 !important;
    }
    .b-mail-dropdown__item__content {
        color: #909090 !important;
    }
	.b-mail-dropdown__separator{
		border-top:1px solid #606060 !important;
	} 
    .b-mail-dropdown__item__content:hover {
        background: #444 !important;
    }
	.b-popup__close, .b-mail-dropdown__close {
		line-height: 1em !important;
		background: #333 !important;
		border: 1px solid #606060 !important;
	}
	.b-popup__close:hover, .b-mail-dropdown__close:hover {
		background: #222 !important;
	}
	/* dropdown tail */
	.boxshadow .b-popup__tail_top:after,
	.boxshadow .b-popup__tail_bottom:after,
	.boxshadow .b-popup__tail_left:after,
	.boxshadow .b-popup__tail_right:after {
		content: "" !important;
	}
	.boxshadow .b-popup__tail_top:after {
		border-bottom: 12px solid #333;
		border-left: 8px solid transparent;
		border-right: 8px solid transparent;
	}
	.boxshadow .b-popup__tail_bottom:after {
		border-top: 12px solid #333;
		border-left: 8px solid transparent;
		border-right: 8px solid transparent;
	}
	/* feed chooser */
	.b-mail-ribbon {
		background-color: #404040 !important;
	}
	.b-mail-ribbon_lenta {
		background-image: none !important;
		border-left: 1px solid #606060 !important;
		margin-left: -1px !important;
		margin-bottom: 0 !important;
		background-color: #303030 !important;
	}
	.b-lenta-bundle {
		background-color: #404040 !important;
		color: inherit !important;
	}
	.b-lenta-bundle__feed_current {
		background-color: #333 !important;
	}
	.b-lenta-bundle__more:hover {
		color: #fea !important;
	}
	.b-mail-ribbon__item__i {
		color: inherit !important;
	}
	.b-mail-button_catalogue {
		background-color: #444 !important;
		color: inherit !important;
		border: none !important;
	}
	.b-mail-button_catalogue:hover {
		background-color: #333 !important;
        transition: background-color 0.5s;
		background-image: none !important;
	}
	.b-mail-button_catalogue .b-mail-button__text {
		color: inherit !important;
	}
	/* feed carousel */
	.lenta-catalogue-ribbon {
		background-color: #606060 !important;
	}
	.b-mail-ribbon__arr {
		color: inherit !important;
		height: 60% !important;
		top: 20% !important;
		background: #404040 !important;
        transition: background-color 0.5s;
	}
	.b-mail-ribbon__arr:hover {
		color: #fff !important;
		background: #606060 !important;
        transition: background-color 0.5s;
	}
	.b-mail-ribbon__arr_left {
		border-top-right-radius: 50px;
		border-bottom-right-radius: 50px;
	}
	.b-mail-ribbon__arr_right {
		border-top-left-radius: 50px;
		border-bottom-left-radius: 50px;
	}
	.b-mail-ribbon__arr_left .b-mail-ribbon__arr__i {
		margin-left: -15px !important;
	}
	.b-mail-ribbon__arr_right .b-mail-ribbon__arr__i {
		margin-left: -5px !important;
	}
}
