css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("2chan.net"){
/*a.del, */
[src*="http://free2.jp/2chan_2/404"],
[style="width:336px;height:280px;margin:2px"],
.delform,
[width="468"],
[src="i.png"],
[target="_top"],
[href="http://www.2chan.net/ktinv.htm"],
[id*="akahuku_catalog_reorder_container"],
[style="width:468px;height:60px;margin:2px"],
[style="width:728px;height:90px;margin:2px"],
[style="width:728px;height:90px;margin:2px auto"],
body > [href="futaba.htm"],
[align="center"] > small,
[type="checkbox"],
[align="right"],
[name="pwd"],
[name="sub"],
[name="email"],
[name="name"],
#ama404,
#ama4042,
.ama,
.ad,
#adspotdiv.adspot_img,
#akahuku_sagebutton,
#akahuku_commentbox_status,
#akahuku_floatpostform_footer_content_1,
#rightad,
#ninja_iframe_1,
.center,
#a35111d5,
#akahuku_dummyid_16,
.ftbl small,
th,
b,#tit,#hml,#hdp,
.ftdc
                {display:  none  !important;}
/*center */
form        {width: 750px       !important;
             margin-left:auto   !important; 
             margin-right:auto  !important;
             text-align:left    !important;}

a.del           {color: #000000  !important;}

/* COLOR SET */
body, 
.ftbl :first-line,
#akahuku_reload_container,
[class*="akahuku_generted"],          
[href="http://www.2chan.net/ktinv.htm"]
                {color: #000000  !important;}      
blockquote      {color: #E69CE4  !important;}
     
#akahuku_new_reply_header_number
                {color: #01DFD7  !important;} 
td,
body,
blockquote,
#akahuku_floatpostform_footer,
#akahuku_reload_container,
#akahuku_savemht_button
     {background-color: #00000f  !important;} 

[class*="akahuku_preview_button"],
.akahuku_saveimage_button,     
#akahuku_bottom_status,
.akahuku_cell                                
                {color: orange  !important;}   

[id^="akahuku_catalog_reload_button"],
[id^="akahuku_catalog_reload_redo"],
[id^="akahuku_catalog_reload_undo"],
.akahuku_catalog_reorder,
[id*="akahuku_commentbox_status_commentbox_"],
#akahuku_reload_button
                {color: #00FF00 !important;}       
span.akahuku_replynumber,                
.akahuku_popup_content_blockquote,
[class*="akahuku_status_suffix_"],
#akahuku_floatpostform_close_button,
#akahuku_thread_text
                {color: pink    !important;}       

.akahuku_generated_link
                {color: #789922 !important;} 

.akahuku_deleteimage_button,
.akahuku_srcimage_button,
.akahuku_thumbimage_button,  /* deeppink */ 
#akahuku_thread_deletetime,
#akahuku_reload_status, 
.akahuku_popup_content_button
                {color: #FF1493 !important;}  

#akahuku_thread_warning
                {color: red     !important;}
                
li              {
background-color: #555252   !important;    
width: 105% !important;
}

.akahuku_native_comment{color: pink !important;}
#oebtn, :link   {color: plum    !important;} 
:visited        {color: brown   !important;}

.chui font,
.chui li{color: #fff       !important;
        font-size: 110% !important;}
.chui a{color: orange !important;}
/*
[name="upfile"] :empty
     {background-color: pink    !important;}
*/
[method="POST"], #akahuku_reload_syncbutton
                {color: #808080 !important;} 

td a.akahuku_visited font, 
td a:visited font, 
td[__opened] a font
     {background-color: red     !important;} 

#akahuku_thread_deletetime{padding-left: 300px !important;  }

[href*="futaba.php?mode=cat"]
                {color: orange !important;}
                      
[id*="akahuku_bottom_status_"]
                {color: #f0fff0 !important;}

font[color="#789922"]
                {color: #2EFE2E !important;}

/* ************************************** */
#akahuku_catalog_reload_container{
        font-size: 14pt         !important;}

#akahuku_catalog_reload_container2{
        font-size: 14pt         !important;
        text-align: center      !important;}

[src^="/del.php?b=b&d="]
                { width: 250px  !important;
                 height: 500px  !important;}
.akahuku_saveimage_button2                 
                  {color:#ee82ee !important;}

.psen [method="get"]{width:100% !important;}

.psen      {padding-left: 200px !important;}

/*
[name="upfile"]{
    letter-spacing:     2px     !important;
    }
*/

#akahuku_floatpostform_close_button{
    letter-spacing:    150px     !important;
    float:right                   !important; 
    position: relative           !important;
    font-size:         110%      !important;} 

#akahuku_floatpostform_footer_content
    {background-color: pink         !important;}

           
#akahuku_postform{
    background-color: #000   !important;
    }

#akahuku_postform_submitter{
    padding-left:      4px      !important;
    width:             101%     !important;
    font-size:         12pt     !important; 
    font-weight:       bold     !important;
    letter-spacing:    32px     !important;
    }
}

/*                  del               */
@-moz-document 
    url-prefix("http://may.2chan.net/del"){
    h1,[action=""], hr,a
    {display: none !important;}    
    td{color: deeppink !important;}
    body{   color: #000000 !important;
            font-size: 1pt !important;}
}
/*xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx
                 catlog
xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx*/
@-moz-document 
    url-prefix("http://may.2chan.net/b/futaba.php?mode"),
    url-prefix("http://img.2chan.net/b/futaba.php?mode"),
    url-prefix("http://dec.2chan.net/b/futaba.php?mode"),
    url-prefix("http://jun.2chan.net/b/futaba.php?mode"),
    url-prefix("http://dat.2chan.net/b/futaba.php?mode"),
    url-prefix("http://dec.2chan.net/18/futaba.php?mode"),   /*jikkyopu*/
    url-prefix("http://cgi.2chan.net/m/futaba.php?mode"),   /*suugaku*/
    url-prefix("http://cgi.2chan.net/k/futaba.php?mode"),   /*kabegami*/
    url-prefix("http://cgi.2chan.net/l/futaba.php?mode")   /*kabegami2*/    
{
hr{display: none !important;}

td{  
    background-color: #000000   !important;
               color: pink      !important;}       
font          {color: #00ff00   !important;}                                             

td:nth-child(2n + 1)
    {background-color: #3B170B  !important;}   
td:nth-child(2n + 2)
    {background-color: #3B0B39  !important;}  
}
/*    kiyaku  */
@-moz-document
url("http://www.2chan.net/kiyaku.htm"){

body,li{         color:#fff        !important;
  background-color: #000        !important;}    

body        {width: 750px       !important;
             margin-left:auto   !important; 
             margin-right:auto  !important;
             text-align:left    !important;}
}
/*  config   */
@-moz-document 
    url-prefix("http://may.2chan.net/b/futaba.php?mode=catset")
{
        body{padding-top: 130px !important;}

}

/*xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx
                 res
xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx*/  
@-moz-document 
    url-prefix("http://may.2chan.net/b/r"),
    url-prefix("http://img.2chan.net/b/r"),
    url-prefix("http://dec.2chan.net/b/r"),
    url-prefix("http://jun.2chan.net/b/r"),
    url-prefix("http://dat.2chan.net/b/r"){
    
hr, .chui,
[align="center"],
#akahuku_postform_opener_appendix,
label,                            
#akahuku_floatpostform_list
{display: none !important;}  
   
small,
td[style*="padding-top: 0px;"] 
                {color: #000000 !important;}

td             {color: #555252  !important;}
}
