css:
@-moz-document domain("www.nicovideo.jp") {
#zero_lead_index{ display:none !important; }
}

@-moz-document url-prefix("http://www.nicovideo.jp/my/") {
#myContHead
{
  padding: 0px 0px;
}

#myContHead a.exLink { display: none !important; }

#myContBody
{
  padding: 0px 10px;
}

#myContBody ul.myContList li .repThumb, .repThumb img, .repThumb a
{
  width: 50px !important;
  height: 38px !important;
}

#myCont ul.myContList li .time.imdt
{
  color: #666 !important;
}

.nofix #header { display: none !important; }

.comment.noRes
{
  position: relative;
  width: 81px;
  left: 0px;
}

.commentBtn
{
  position: relative;
}

.commentBtn a
{
  background-color: #f3f3f3 !important;
  background-image: none !important;
  border: #cccccc 1px solid !important;
  font-size: 10px !important;
  color: #000001 !important;
}

.commentBtn a:hover
{
  background-color:#fcfcfc !important;
}

.report .userName a
{
  color: #000 !important;
  font-weight: normal;
  font-size: 11px;
}

.highlight a:visited
{
  color: #440066 !important;
}

.highlight, .highlight a
{
  color: #f00;
}
}

@-moz-document url-prefix("http://www.nicovideo.jp/watch/sm") {
#PAGEHEADER
{
  height: 0px;
}

#head_search
{
  position: absolute;
  top: -9px;
  right: 1px;
}



#head_logo { display: none !important; }

.des_2 img[src^="http://res.nimg.jp/img/watch/btn"]
{
  position: absolute;
  top: 60px;
  right: 5px;
}

.owner_prof
{
  position: absolute;
  top: 85px;
}
.channel_open_box
{
	position: absolute;
  	top: 100px;
  	left: 990px;
}

#mylistlinklist { display: none !important; }





a[href^="http://uad.nicovideo.jp/ads/?vid=sm"]
{
  position: relative;
  top: 40px;
  left: 260px;
}

a[href*="/mylist_add/video/sm"]
{
  position: relative;
  top: 75px;
  left: 180px;
}

a#BTN_add_deflist
{
  position: relative;
  top: 110px;
  left: 100px;
}

#head_ads { display: none !important; }
}
