css:
@-moz-document url("http://www.priori-incantatem.sk/chat/hlavicka.php") {
body {
  background: #000 url('http://i254.photobucket.com/albums/hh111/ed_photoalbum/grafika/layouts/pi-tk-ch/23906961_zpstobekcox.jpg') 0 top no-repeat !important;
  padding: 0;
  margin: 0;
}

img[src="pictures/hr.gif"], img[src*="pictures/chat_logo_"] {
  display: none;
}

img[src*="pictures/chat_logo_01"] {
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 0;
  height: 0;
  padding-top: 66px;
  padding-left: 600px;
  background: url("http://i254.photobucket.com/albums/hh111/ed_photoalbum/grafika/layouts/pi-tk-ch/49bn_zpsghslbo84.png") left center no-repeat;
}

table {
  height: 100%;
  background: rgba(100, 0, 0, 0.2);
}
}

@-moz-document url("http://www.priori-incantatem.sk/chat/main_.php") {
body, table, font[color="black"] {
  color: #c2c2c2 !important;
}

table {
  background: transparent  !important;
  font-size: 10px !important;
}

table tr:first-child {
  display: none;
}

tr td:nth-child(2) {
  white-space: nowrap;
  width: 100px;
  max-width: 100px;
  overflow: hidden;
}

/*tr[bgcolor*="2F423C"]*/ .poz_sv {
  background: rgba(150,0,0,.2) !important;
}

/*tr[bgcolor*="385249"]*/ .poz_tm {
  background: rgba(200,50,0,.2) !important;
}

tr[bgcolor*="604D5B"], tr[bgcolor*="4d657d"] {
  background: rgba(200,50,0,.5) !important;
}


tr td:first-child {
  background: transparent !important;
  width: 100px !important;
}

img {
  max-height: 15px;
}


body {
  background: #000 url('http://i254.photobucket.com/albums/hh111/ed_photoalbum/grafika/layouts/pi-tk-ch/23906961_zpstobekcox.jpg') 0 -64px no-repeat !important;
  padding: 0;
  margin: 0 !important;
}

tr td:nth-child(3) {
  background: url('data:image/png;base64,') top right no-repeat !important;
}

img[src*="tajne.png"] {
	width: 0;
	height: 0;
  	padding-top: 25px;
  	padding-left: 20px;
    background: url("http://i254.photobucket.com/albums/hh111/ed_photoalbum/grafika/layouts/pi-tk-ch/ptyy_zpssfu3sleq.png") center center no-repeat;
}
}

@-moz-document url("http://www.priori-incantatem.sk/chat/input.php") {
body {
  margin: 0;
  padding: 0;
  background: #140000!important;
}

img[src="pictures/hr.gif"] {
  visibility: hidden;
}

input, select {
  background-color: #440000 !important;
  border: 1px #940000 solid !important;
  color: #c2c2c2 !important;
}
}

@-moz-document url("http://www.priori-incantatem.sk/chat/users_.php") {
body {
  margin: 0;
  padding: 0;
  background: #140000 !important;
}

table {
  width: 100%;
}

.logout_table, tr[bgcolor*="385249"] {
  background: rgba(200,50,0,.2) !important;
  border: 1px rgba(250,100,0,.6) solid !important;
}

a.logout:hover {
  background: rgba(200,50,0,.5) !important;
}
    
table:last-child {
  border: 1px rgba(250,100,0,.6) solid !important;
}
    
table:last-child td:first-child {
  border-bottom: 1px rgba(250,100,0,.6) solid !important;
}
}
