css:
@-moz-document url-prefix("http://weibo.com/") {
* {
	-webkit-font-smoothing: antialiased;
}


div[ad-data] {
	display: none;
}


.WB_global_nav {
    margin-top: -20px;
    
    
    -webkit-transition: all 200ms 0 ease-in-out;
	-moz-transition: all 200ms 0 ease-in-out;
	-ms-transition: all 200ms 0 ease-in-out;
	-o-transition: all 200ms 0 ease-in-out;
	transition: all 200ms 0 ease-in-out;
}
.WB_global_nav .gn_bg {
	background-color: #333;
    background-image: none;
}
.WB_global_nav:hover {
    margin-top: 0;
	opacity: 1;
}

.gn_nav div[node-type="app"], .gn_nav div[node-type="game"], .gn_nav div[node-type="home"] {
	display: none;
}


#pl_leftnav_app {
	display: none;
}


#trustPagelet_recom_interestv5 {
	display: none;
}
#trustPagelet_recom_memberv5 {
	display: none;
}
#trustPagelet_recom_widthinonev5 {
	display: none;
}
#trustPagelet_recom_allinonev5 {
	display: none;
}
#pl_rightmod_noticeboard {
	display: none;
}
#trustPagelet_zt_hottopicv5 {
	display: none;
}

#Pl_Rightmod_Helpbox, #pl_rightmod_feedback, #pl_common_reportentry, #trustPagelet_indexright_recom {
	display: none;
}


.title_area {
	display: none;
}
.key.S_textb {
	display: none;
}
.W_main_c {
	padding-top: 0 !important;
}


.WB_feed_datail.S_line2 {
	border-color: rgba(230,230,230, .5);
}


.W_main_l {
	width: 15px;

	-webkit-transition: width 200ms 0 ease-in-out;
	-moz-transition: width 200ms 0 ease-in-out;
	-ms-transition: width 200ms 0 ease-in-out;
	-o-transition: width 200ms 0 ease-in-out;
	transition: width 200ms 0 ease-in-out;
	
}
.W_main_l>* {
	-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=0)";
	filter: progid:DXImageTransform.Microsoft.Alpha(Opacity=0);
	opacity: 0;

	-webkit-transition: all 300ms 0 ease-in-out;
	-moz-transition: all 300ms 0 ease-in-out;
	-ms-transition: all 300ms 0 ease-in-out;
	-o-transition: all 300ms 0 ease-in-out;
	transition: all 300ms 0 ease-in-out;
}


.B_index .W_main, .B_message_box .W_main{
	width: 845px;
}
.W_main_a {
	width: auto;

	-webkit-transition: width 200ms 0 ease-in-out;
	-moz-transition: width 200ms 0 ease-in-out;
	-ms-transition: width 200ms 0 ease-in-out;
	-o-transition: width 200ms 0 ease-in-out;
	transition: width 200ms 0 ease-in-out;
}
.W_main_c {

	-webkit-transition: width 200ms 0 ease-in-out;
	-moz-transition: width 200ms 0 ease-in-out;
	-ms-transition: width 200ms 0 ease-in-out;
	-o-transition: width 200ms 0 ease-in-out;
	transition: width 200ms 0 ease-in-out;
}


.WB_func {
	padding: 5px 10px;
	background-color: rgba(210,210,210, .08);
}


textarea.W_input {
	width: 100% !important;
}


#Box_right {
	position: fixed;
	margin-left: 595px;
	-webkit-box-shadow: 0 0 5px rgba(0,0,0,.2);
	-moz-box-shadow: 0 0 5px rgba(0,0,0,.2);
	box-shadow: 0 0 5px rgba(0,0,0,.2);

	-webkit-transition: all 200ms 0 ease-in-out;
	-moz-transition: all 200ms 0 ease-in-out;
	-ms-transition: all 200ms 0 ease-in-out;
	-o-transition: all 200ms 0 ease-in-out;
	transition: all 200ms 0 ease-in-out;
}
#Box_right .W_person_info dt {
	float: none;
	text-align: center;
	width: auto;
	margin: 0;
	background: none;
}
#Box_right .W_person_info dt img {
	-webkit-border-radius: 80px;
	-moz-border-radius: 80px;
	border-radius: 80px;
}
#Box_right .W_person_info .nameBox {
	text-align: center;
}
#Box_right .user_atten {
	text-align: center;
}
#Box_right .user_atten li {
	display: inline-block;
	float: none;
}

.gn_topmenulist.gn_tips {
	width: 230px;
	position: fixed;
	margin-left: 670px;
	left: auto;
	top: 325px !important;
	border: 0 !important;
	font-size: 14px;
	-webkit-box-shadow: 0 0 5px rgba(0,0,0,.2);
	-moz-box-shadow: 0 0 5px rgba(0,0,0,.2);
	box-shadow: 0 0 5px rgba(0,0,0,.2);

}

.W_main_l:hover {
	width: 150px;
}
.W_main_l:hover>* {
	-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=100)";
	filter: progid:DXImageTransform.Microsoft.Alpha(Opacity=100);
	opacity: 1;
}
.W_main_l:hover + #plc_main #Box_right {
	opacity: 0;
}


.WB_feed_together {
	display: none;
}

.WB_screen {
	margin-left: 540px !important;
}

.B_index .notes {
	width: 230px;
	position: fixed;
	margin-left: 593px !important;
}

.B_message_box .WB_right_module {
	display: none;
}


.WB_feed_type .S_line2, .WB_msg_type .S_line2 {
	border: 0px !important;
}


#base_scrollToTop {
	top: 130px;
	margin-left: 380px;
}


.WB_msg_type:hover {
	background-color: #f2f2f2;
}




.B_send_me #plc_main, .B_message_box #plc_main {
	background-color: rgb(253,253,253);
}
.B_send_me #Box_right, .B_message_box #Box_right {
	display: none;
}
}
