css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
body {

background: url('http://24.media.tumblr.com/a9c4e84a65a5628e9ac1c6c01b70705f/tumblr_mj25ydAFkF1r8yl1fo1_r6_1280.jpg') center right #000000 repeat fixed !important;

}

ol#posts li blockquote,

body.mceContentBody blockquote {

margin-left: 0 !important;

margin-right: 0 !important;


padding-left: 10px !important;

border-width: 4px !important;

border-color: #000000 !important;

}

ol#posts li blockquote blockquote,

body.mceContentBody blockquote blockquote {

border-color: #000000 !important;

}

ol#posts li blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote {

border-color: #000000 !important;

}

ol#posts li blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote {

border-color: #000000 !important;

}

ol#posts li blockquote blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote blockquote {

border-color: #000000 !important;

}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background color: #ffffff; !important;


  }

#logo {
height: 0 !important;
width: 0 !important;
padding-left: 240px !important;
padding-top: 70px !important;
background: url(http://25.media.tumblr.com/f3e2b19a4a704ecfc2b481198495ad70/tumblr_mj0dgkE4pc1r8yl1fo1_r1_250.png) no-repeat !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:#FFF!important;
margin-top: 0px !important;
border-radius:0px !important;
padding-top:45px!important;
text-shadow:none !important;}

*{text-shadow:none !important; color:#626262!important;}

.tab_notice {top: 0px !important;
background-image:none!important;
margin-left: 4px !important;
height:auto!important;
width:auto !important;
text-align: center !important;
border-radius: 6px !important;
box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.7) !important;
vertical-align:middle !important;
font:11px consolas !important;
font-weight: bold !important;}

*.tab_notice_value{text-shadow:none !important; color:#F8E9E9!important;}

.tags{font-size:11px!important;margin-top:0px!important;
font-family:consolas;vertical-align:middle !important;}
.source_url{font-size:11px!important;
font-family:consolas!important;
text-transform:uppercase;
vertical-align:middle !important;
margin-right:4px!important;}
.footer_links{margin-top:20px!important;
vertical-align:middle !important;}


#posts LI[class*=" post"] {box-shadow: none !important; border: 1px solid #E7EAEC !important;border-radius:0px !important;}

#posts LI[class*=" post"]:hover {box-shadow: none !important; border: 1px solid #BCBCBC !important;border-radius:0px !important;}

#posts LI[class*="notification"] {box-shadow: none !important;
border: 1px solid #E7EAEC !important;
border-radius:0px !important;
background-color:#FFF !important;
border-bottom: 1px solid #E8E8E8!important;
border-top: 0px solid #333!important;
border-left: 0px solid #333!important;
border-right: 0px solid #333!important;}

#posts LI[class*="notification"]:hover {box-shadow: none !important;
border: 1px solid #E7EAEC !important;
border-radius:0px !important;
background-color:#FFF !important;
border-bottom: 1px solid #BCBCBC!important;
border-top: 0px solid #E1E1E1!important;
border-left: 0px solid #E1E1E1!important;
border-right: 0px solid #E1E1E1!important;}

.post_info A,
.post_controls A,
.footer_links.with_tags SPAN A {
color:#333333 !important;}

.camera {background: transparent !important;
border-radius: 0px !important;
opacity: 0.7 !important;
padding-bottom: 4px;}

.image { display: block !important;
margin-left: auto !important;
margin-right: auto !important;}

#dashboard_controls_open_blog{background-color:#F0F0F0!important;
box-shadow: none !important;
border: 1px solid #D7D7D7!important;
border-radius:0px !important;}

.controls_section{background-color:#F0F0F0!important;
box-shadow: none !important;
border: 1px solid #D7D7D7!important;
border-radius:0px !important;}

li.selected {box-shadow: none !important;
border-bottom: 0px solid #D7D7D7!important;
border-radius:0px !important;}

 


div.return_to_top_icon{	background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 30px black !important;
border-right: 15px solid transparent !important;
border-left: 15px solid transparent !important;}


h1 {

color: #131313;

text-shadow: 0 0 3px #000000;

}

.no_posts_found, h1.dashboard_header {

color: #000000;

}

img#content_top, img#content_bottom {

display: none;

}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {

    background: none;


  }

#right_column .dashboard_nav_item {

    background: none repeat-x;

    background-attachment: initial;

    background-position-x: 50%;

    background-position-y: 100%;

    background-origin: initial;

    background-clip: initial;

    background-color: initial;

}

#right_column .dashboard_nav_item #hide_radar {

background-image: none;

background-repeat-x: no-repeat;

background-repeat-y: repeat;

background-attachment: initial;

background-position-x: 0%;

background-position-y: 0%;

background-origin: initial;

background-clip: initial;

background-color: #131313;

}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background: rgba(0,0,0,0.0);

}

#nav .nav_item.active {

    background: rgba(00,0,0,1) !important;

  }

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

    background: rgba(00,0,0,0.5) !important;

  }

ol#posts li.notification.alt {

    background-color: rgba(0,0,0,0.5);

}

ol#posts li.notification.first_notification {

    background-color: rgba(0,0,0,0.5);

}

ol#posts li.notification {

    background-color: rgba(0,0,0,0.5);

    border-bottom: 1px solid #000000;

}

ol#posts li.notification.last_notification {

    border-bottom: 1px solid #000000;

}

ol#posts li.post .permalink {

    background: rgba(00,0,0,0.5);

}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {

background: rgba(0,0,0,0.8);

border: solid #000000;

}

ol#posts li.post .post_avatar{

border-bottom: 0px solid #000000;

}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {

border-top: 1px solid #000000;

border-bottom: 1px solid #000000;

}

#right_column a#dashboard_switch_blog_menu_current:hover {

background: none;

}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {

background: none;

}

form.dashboard_options_form {

background-color: rgba(0,0,0,0.0);

border-bottom: 1px solid #000000;

border-bottom-left-radius: 10px 10px;

border-bottom-left-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-right-radius: 10px 10px;

border-top-right-radius: 10px 10px;

color: #000000;

display: block;

font-size: 13px;

margin-bottom: 25px;

padding: 15px 22px;

}

form.dashboard_options_form .option_container {

border-top: 1px solid #000000;

}

.no_posts_found, h1.dashboard_header {

color: #131313;

}



.new_post_label .new_post_label_icon {


background-image:none !important;}
#new_post{background: url('http://25.media.tumblr.com/c7603b394120653d0bf6fcc5084b4aed/tumblr_mj290sglAA1r8yl1fo1_1280.jpg') center no-repeat; background-color:none !important;}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:0px;
    opacity: 0;
    display: block;}
.new_post_label{
    font-size:0px !important;
    padding-top:0px !important;
    opacity: 0;}


.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px #fff !important;}



.arrow {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 9px #000 !important;
border-bottom: 9px solid transparent !important;
border-left: 2px solid transparent !important;
border-top: 9px solid transparent !important;
margin-top: 0px !important;
}
.nipple {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 6px #000 !important;
border-bottom: 6px solid transparent !important;
border-left: 0px solid transparent !important;
border-top: 6px solid transparent !important;
margin-top: 0px !important;
}
.nipple.border {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 6px #000 !important;
border-bottom: 6px solid transparent !important;
border-left: 1px solid transparent !important;
border-top: 6px solid transparent !important;
margin-top: 1px !important;}



.notification, .answer_container, .answer_input, .dashboard_options_form,
.flat{border: 0px solid #FF00FF !important;}


#search_query {background:#F0F0F0 !important;border-radius:6px !important;}


#left_column #posts .post .permalink{background-color:transparent!important;}


#left_column #posts .notification {background-color:transparent!important;}
}
