css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
body {background-color: #000;
background-image: url('http://25.media.tumblr.com/702e70f54b756f30ccbdd9d4d4ba44b1/tumblr_mov5q5mE8V1r7rdego1_400.png'), url('http://24.media.tumblr.com/894e198bfa746488368c721d39093f28/tumblr_mov41jRuNr1r7rdego2_400.png'), url('http://24.media.tumblr.com/710173f15b69a3ac4ddb33f9d0de4eb8/tumblr_mov41jRuNr1r7rdego1_1280.png');
background-position: bottom left, bottom right, bottom right ;  
background-attachment: fixed !important;
background-repeat: no-repeat, no-repeat,repeat;}


#logo {height: 0 !important;
width: 0 !important;
padding-left: 240px !important;
padding-top: 70px !important;
padding-bottom: 30px !important;
background: url(http://25.media.tumblr.com/198707172b5a08aa236eaad7573f6d96/tumblr_mov4wqlnah1r7rdego2_250.png) no-repeat !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(248,248,255, .50) !important;
}

*{text-shadow:none !important; color:#000 !important;}

#dashboard_switch_blog_menu_current,


#dashboard_switch_blog_arrow_and_divider,


#dashboard_switch_blog_arrow_current


{background-image:none !important}


.selection_nipple.white, 
.selection_nipple:not([class="selection_nipple white"]) { 
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px white !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important; }

}

@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
img[src*="http://25.media.tumblr.com/af72d71b77714780c02cc778304c0054/tumblr_mov5yfDjEn1r7rdego1_1280.png"],
.new_post_label_icon {
    opacity: 0 !important;
}

#new_post_label_text,
#new_post_label_photo,
#new_post_label_quote,
#new_post_label_link,
#new_post_label_chat,
#new_post_label_audio,
#new_post_label_video {
  background: rgba(255,255,255, 1.0) url("http://25.media.tumblr.com/af72d71b77714780c02cc778304c0054/tumblr_mov5yfDjEn1r7rdego1_1280.png") no-repeat !important;
}

#new_post_label_text {
  background-position: 2px 0px !important;
  border-top-left-radius: 8px !important;
  border-bottom-left-radius: 8px !important;
}
#new_post_label_photo {
  background-position: -73px 0px !important;
}
#new_post_label_quote {
  background-position: -148px 0px !important;
}
#new_post_label_link {
  background-position: -223px 0px !important;
}
#new_post_label_chat {
  background-position: -298px 0px !important;
}
#new_post_label_audio {
  background-position: -373px 0px !important;
}
#new_post_label_video {
  background-position: -448px 0px !important;
  border-top-right-radius: 8px !important;
  border-bottom-right-radius: 8px !important;
}



#new_post_label_text:hover,
#new_post_label_photo:hover,
#new_post_label_quote:hover,
#new_post_label_link:hover,
#new_post_label_chat:hover,
#new_post_label_audio:hover,
#new_post_label_video:hover {
  background-color: rgba(0,0,0, .05) !important;
  color: #551A8B !important;
}

#new_post_label_text:hover {
  background: url('http://25.media.tumblr.com/af72d71b77714780c02cc778304c0054/tumblr_mov5yfDjEn1r7rdego1_1280.png') 2px -95px !important;
}
#new_post_label_photo:hover { 
  background: url('http://25.media.tumblr.com/af72d71b77714780c02cc778304c0054/tumblr_mov5yfDjEn1r7rdego1_1280.png') -73px -95px !important;
}
#new_post_label_quote:hover { 
  background: url('http://25.media.tumblr.com/af72d71b77714780c02cc778304c0054/tumblr_mov5yfDjEn1r7rdego1_1280.png') -148px -95px !important;
}
#new_post_label_link:hover { 
  background: url('http://25.media.tumblr.com/af72d71b77714780c02cc778304c0054/tumblr_mov5yfDjEn1r7rdego1_1280.png') -223px -95px !important;
}
#new_post_label_chat:hover { 
  background: url('http://25.media.tumblr.com/af72d71b77714780c02cc778304c0054/tumblr_mov5yfDjEn1r7rdego1_1280.png') -298px -95px !important;
}
#new_post_label_audio:hover {
  background: url('http://25.media.tumblr.com/af72d71b77714780c02cc778304c0054/tumblr_mov5yfDjEn1r7rdego1_1280.png') -373px -95px !important;
}
#new_post_label_video:hover {
  background: url('http://25.media.tumblr.com/af72d71b77714780c02cc778304c0054/tumblr_mov5yfDjEn1r7rdego1_1280.png') -448px -95px!important;
}

}

@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
/*NOTIFICATION BARS begin*/
.notification.single_notification,
.notification.first_notification,
.notification.last_notification,
.notification,
.avatar_frame,
.answer_container, 
.notes LI, .private_label {
  background: #000 !important;
 
}
.notification.alt {
    background: #000 !important;
}
.notification.single_notification,
.notification.first_notification,
.avatar_frame,
.notes LI, .private_label {
  
}
/*NOTIFICATION BARS end*/

/*NOTIFICATION BAR ARROW begin*/
.nipple {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(246,221,233,.95) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 0px !important;
  margin-left: -1px !important;
}
.nipple.border {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(160,72,113, .75) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 1px !important;
  margin-left: 0px !important;
}
/*NOTIFICATION BAR ARROW end*/
}
