css:
@-moz-document url-prefix(http://apps.facebook.com/packrat), url-prefix(http://apps.new.facebook.com/packrat) {
 div#app2431403991_afire-header h1, .app_content_2431403991 .discuss-wrapper #app2431403991_VE_banner {
   display: none !important;
   visibility: hidden !important;
 }
 /* Just hide the ads */
 #sidebar_ads {
   display: none !important;
   visibility: hidden !important;
 }
 /* Score board */
 div#app2431403991_afire-scoreboard {
   z-index: 100 !important;
   position: fixed !important;
   top: 55px !important;
   right: auto !important;
   left: auto !important;
   margin-left: 765px !important;
   background-position: top right !important;
 }
 /* Yellow news text box */
 div.app_content_2431403991 div.packrat-news {
   z-index: 100 !important;
   position: fixed !important;
   top: 130px !important;
   right: auto !important;
   left: auto !important;
   width: 200px;
   margin-left: 765px !important;
   background-position: top right !important;
 }
 div.app_content_2431403991 {
   width: 760px !important;
 }
 /* Credit Popup */
 div.app_content_2431403991 .grab-credit, div.app_content_2431403991 .grab-credit.p1, div.app_content_2431403991 .grab-credit.p2, div.app_content_2431403991 .grab-credit.p3, div.app_content_2431403991 .grab-credit.p4 {
   position: fixed !important;
   top: 55px !important;
   right: auto !important;
   left: auto !important;
   margin-left: 650px !important;
 }
 /* Card Popup */
 div.app_content_2431403991 .grab-item, div.app_content_2431403991 .grab-item.p1, div.app_content_2431403991 .grab-item.p2, div.app_content_2431403991 .grab-item.p3, div.app_content_2431403991 .grab-item.p4 {
   position: fixed !important;
   top: 0px !important;
   right: auto !important;
   left: auto !important;
   margin-left: 470px !important;
 }
 /* Market nav buttons */
 .app_content_2431403991 #app2431403991_afire-banner {
   padding-top: 0 !important;
 }
 .app_content_2431403991 .market-wrapper .market-image img, .app_content_2431403991 .browse-widget img, .app_content_2431403991 .user .user-image img {
   height: 25px !important;
   width: 25px !important;
 }
 .app_content_2431403991 .browse-widget .arrow-l, .app_content_2431403991 .browse-widget .arrow-r, .app_content_2431403991 .browse-widget .browse-left a, .app_content_2431403991 .browse-widget .browse-right a {
   height: 25px !important;
 }
 .app_content_2431403991 .market-wrapper .market p.viewall, .app_content_2431403991 .market-wrapper .market-info, .app_content_2431403991 .user p {
   padding-top: 0 !important;
 }
 .app_content_2431403991 .user {
   padding-bottom: 0 !important;
 }
 .app_content_2431403991 .browse-widget .browse-slider-container {
   height: 55px !important;
 }
 /* Chat/Notification window overlay */
 div#presence {
   z-index: 150 !important;
 }
}
