css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("narutoforums.com") {
.tborder, .thead, .tcat, .tfoot, .panel { border: 0 !important; border-spacing: 0 !important; }
.thead, .alt2, .alt1, .vBulletin_editor, fieldset { border: 0 !important; }
[style*="padding:1px"] { padding: 0px !important; }

.tborder, iframe { box-shadow: 0px 0px 5px rgba(208,208,208,0.236); }
.alt1, .alt1Active, .alt2, .alt2Active, .thead, .tfoot, .tcat, .panelsurround { box-shadow: inset 0px 0px 6px rgba(80,80,80,0.75); }
input, select, textarea { box-shadow: 0px 0px 5px rgba(240,240,240,0.618); }
img[src^="customavatars"] { box-shadow: 0px 0px 5px rgba(208,208,208,0.618); } /* pleb stock avs are not shadowed */

.alt1, .alt1Active { background: #202020 !important; color: #d0d0d0 !important; } 
.alt2, .alt2Active { background: #303030 !important; color: #d0d0d0 !important; }
.tcat { background: #404040 !important; }
.thead { background: #505050 !important; }
.vbmenu_control { background: #585858 !important; }
.tfoot { background: #606060 !important; }

body, .page, .page-content, .content, .tborder { background: #202020 !important; }
#content_container, #content, #sidebar_container { background: #202020 !important; }
.panelsurround, .panel, .vBulletin_editor, .imagebutton { background: #303030 !important; color: #d0d0d0 !important;}
.panel [style*="rgb(225, 225, 226)"] { background: #303030 !important; }
.page { background: inherit !important; }
.imagebutton:hover { background: #606060 !important; }

.page, .thead, .tcat, .tfoot, .notice { color: #d0d0d0 !important; }
a { color: #d0d0d0 !important; }
a:visited, .time { color: #a0a0a0 !important; }
a:hover, legend { color: #f0f0f0 !important; }

.vbmenu_option { background: #202020 !important; color: #d0d0d0 !important; }
.vbmenu_hilite { background: #303030 !important; color: #f0f0f0 !important; }
.vbmenu_popup { border: #d0d0d0 !important; color: #202020 !important; }
}
