css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
body {

background: #060623  url('http://24.media.tumblr.com/bb33b6329f26e7ec22bce2ee2c04fc25/tumblr_mox1y8gOUs1rdbl7ho1_1280.png')no-repeat fixed !important;

background-attachment: fixed !important;

background-position: 0% 0% !important;

background-size: 100% 100%  !important;


}





ol#posts li.notification {



  background-color: #f0f0f7   !important;



  border-top: 1px solid #060623 !important;



  border-left: 1px solid #060623 !important;



  border-bottom: 1px solid #060623 !important;



  border-right: 1px solid #060623 !important;



  color: #060623 !important;



  text-decoration: none !important;



}






#posts .notification a:not(.avatar_frame) {



color: rgba(00, 00, 00, 1) !important;



font-weight: bold;



text-decoration: none !important;



border-bottom: 0px solid #060623 !important;



-webkit-background-clip: padding-box;



}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  background: rgba(6, 6, 35, .5) !important; 


}




.selected .following,



.selected .followers,



.selected .settings,



.selected .posts,



.selected .messages,



.selected .drafts,



.selected .queue,



.selected .likes  {



  color: rgba(0, 0, 0, 0.50) !important; 



  background: rgba(184, 164, 200, 0.3) !important; 



}








}
