css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cloud.feedly.com") {

.u0Summary, h1 .hhint, .sourceInfo span a, .sourceTitle, .entryHeader .metadata span span {
    color: black !important;
    font-family: Tahoma !important;
}

#navSelectorHolder, #feedlyPageHeader, #floatingBar, div.entryHeader a.entryTitle {
    background: orange !important;
}

.sourceInfo {
    background: skyblue;  
}

.header, .read { background: rgb(240,240,240) !important; }

/* source list tab styles */
.label { font-weight: bold; }

div.tab .header {
    padding: 0.15em 0.5em 0.15em 0.5em !important;
    border-bottom: 1px solid black !important;
    line-height: 2em !important;
}

.header, .simpleUnreadCount {
    line-height: 2em !important;
    font-size: 1em !important;
}

.title {
    color: black !important;
    padding: 0.5em 0.5em 0.7em 0.5em !important;
}

.title read { 
    font-weight: normal !important;
}

/* hhint */
.categoryUnreadCountHint span {
    color: black !important;
    font-weight: bold;
}

/* useless masturbation */
.hhint .performance {
    display: none;
}

#navSelectorHolder {
    height: 6em !important;
}

#feedlyTitleBar {
    height: 2em !important;
}

#feedlyPageHeader {
    padding: 1em 1em 0em 1em;
    margin: 0 !important;
    height: 5em !important;
}

#floatingBar {
    height: 2.5em !important;
    padding-top: 1em !important;
}

/* annoying 1px white border */
#floatingTitleBar {
    border: 0px solid transparent !important;
}

h1#floatingTitleBar {
    font-size: 2em !important;
}

#feedlyPart0 {
    padding-left: 0px !important;
}

/* ??? */
.area {
    border-width: 0px !important;
}

/*distance to scrollbar */
.u0Entry, .inlineFrame, #fullyLoaded {
    margin-bottom: 0.5em !important;
}

#feedlyPart {
    margin-left: 1.1em !important;
}

/* feed list item row */
.u0Entry {
    border: 1px solid rgb(220,220,220) !important;
    padding: 0 1em 0em 1em !important;
    height: 2.2em !important;
    margin-left: 0 !important;
}

.u0Entry .lastModified {
    padding-top: 0.1em !important;
}

.u0EntryList {
    margin-top: 6px !important;
}

.u0Entry .title, .sourceInfo, .u0Entry .sourceInfo {
    line-height: 2em !important;
}

.u0Entry .quicklistHandle {
    margin-left: -0.6em !important;
    margin-right: 0.2em !important;
}

.sourceInfo {
    padding-right: 0.5em !important;
    padding-left: 0.5em !important;
    margin-top: -0.2em !important;
    padding-bottom: 1em !important;
}

/* feed item opened */
.condensed .inlineFrame {
    padding-left: 2.4em !important;
    border: 1px solid black !important;
    margin-left: 0 !important;
}

/* blue underlined links */
.condensed .inlineFrame .entryBody a {
    color: blue !important;
    text-decoration: underline !important;
}

.entryHeader .metadata a.sourceTitle {
    padding: 1em !important;
}

#myPanel {
    margin-right: 0.95em;
}

/* EOF */
}
