@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp(".*www\\.google(\\.[a-z]{1,4}){1,2}\\/search.*") {

    #search .g .rc .r a[href^="http://stackoverflow.com"],
    #search .g .rc .r a[href^="https://stackoverflow.com"] {
        padding-left: 20px !important;
        background: url(https://cdn.sstatic.net/Sites/stackoverflow/img/favicon.ico) no-repeat -2px 0 / 20px 20px !important;
    }

    #search .g .rc .r a[href^="http://softwareengineering.stackexchange.com"],
    #search .g .rc .r a[href^="https://softwareengineering.stackexchange.com"] {
        padding-left: 20px !important;
        background: url(https://cdn.sstatic.net/Sites/softwareengineering/img/favicon.ico) no-repeat 0 0 / 16px 16px !important;
    }
    
    #search .g .rc .r a[href^="http://programmers.stackexchange.com"],
    #search .g .rc .r a[href^="https://programmers.stackexchange.com"] {
