css:
p,a,a:visited,a:active,td,li,div,body, p font, td {
	font-family: 'Lucida Grande', 'Lucida Sans', 'Lucida Sans Unicode', 'Lucida', Verdana, 'Bitstream Vera Sans', sans-serif !important;
	}
h1, h2, h3, h4, h5, h6, h1 span, h2 span, h3 span, h4 span, h5 span, h6 span {
	font-family: 'Lucida Grande', 'Lucida Sans', 'Lucida Sans Unicode', 'Lucida', Verdana, 'Bitstream Vera Sans', sans-serif !important;
	font-weight:normal;
	page-break-after: avoid !important;
	page-break-inside: avoid !important;
	text-align:left;
	} 
cite{
	text-align:left;
	}
body{
	text-align:justify;
	font-size:12pt;
	}

p, p font, td{
	font-size:12pt !important;
	text-decoration:none !important;
	color:black !important;
	}
a,a:visited,a:active {
	color:#000066 !important;
	text-decoration: none;
	}

a:hover{
	background:lavender !important;
	}
table,tr, input, select,td,img {width:auto !important;max-width:100% !important;height:auto !important;max-height:100% !important;overflow:auto;}

li {
	text-align:left;
	font-size:12pt;
	}
hr {
	height:1pt;
	background:black;
	border:1pt black solid;
	}
@media print{
	p,a,a:visited,a:active,td,li,div,body, p span, td{
		font-family:Georgia,Garamond,'Palatino Linotype','Times New Roman', Times,serif !important;
		}
	body { 		
		-moz-column-count:2;
		-khtml-column-count:2;
		-webkit-column-count:2;
		column-count:2;
		margin:0;
                font-size:80%;
		}
  p {
    margin: 0 0 1em 0;
    }
  p+p {
    margin-top: -1em;
    text-indent: 2em;
    }
  a:link:after, a:link:visited:after {
    content: " (Link auf <" attr(href) ">) ";
    font-size: 76%;
    color: #999;
    background: transparent;
    display:none;
    }
  .references a:link:after, .references a:link:visited:after {
    display:none;
    }
}
@media screen, handheld {
	body { 
		height:97% !important;
		-moz-column-width:250pt;
		-khtml-column-width:250pt;
		-webkit-column-width:250pt;
		column-width:250pt;
		margin:4pt;
                background:#fffff0;
		}
       
	}
