css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.guardiannews.com"), domain("www.guardian.co.uk"),domain("guardiannews.com"), domain("guardian.co.uk") {


#wrapper {
    font-size: 1.3em !important;
    line-height: 1.2 !important;
    color: #686868 !important;
    width: 1100px    !important;
}

.nf-main .large-trail, .single-pickable-tag-badge h4,
.news-trails h3, .auto-trail-list h3, .component h3 {
    font-size: 1em !important;
    line-height: 1.2 !important; 
}
body.blog-index #box #content ul#blog-posts-excerpts li div.blog-excerpt div.trail-caption div.trailtext p, body.blog-index #box #content ul#blog-posts-excerpts li div.blog-excerpt div.trail-caption div.trailtext,
body.blog-index div#wrapper ul#blog-posts-excerpts div.blog-excerpt div.linktext h3 {
    font-size: 1.1em !important;
    line-height: 1.2 !important;
    }
.t7, body.blog-index ul#blog-posts-excerpts li div.blog-excerpt div.trail-caption a.continue-reading {
    font-size: 16px !important;
    }
#live-blog-blocks .block-title, #live-blog-blocks .block-elements {
    font-size: 1.5em !important;
    line-height: 1.2 !important;
    }
ul.l1d {
    width: 250px !important;
    overflow: visible !important;
    }
.ten-col, .col-10, .eight-col, .col-8,
.four-col, .col-4, div.four-col.edge.override   {
    width: 100% !important;
}
.five-col, .col-5 {
    width: 600px !important;
    }
.three-col, .col-3 {
    width: 320px !important;
    }
body.series-index div#content ul#auto-trail-block li.wide {
    width: 660px !important;
    }
body.series-index div#wrapper div#content ul#auto-trail-block li.image .media-above {
    width: 340px !important;
    }
body.blog-index ul#blog-posts-excerpts div.blog-excerpt {
    width: 650px !important;
    }
.network-front .six-col, .col-6,
#live-blog-blocks .block-title, #live-blog-blocks .block-elements  {
    width: 660px !important;
    }
#inner-wrapper, .news-trails .bd, #wrapper, .more-in-series .bd,
.readers-tips .bd  {
    background-image: none  !important;
    border-bottom: none  !important;
    }
body.network-front .four-col, .col-4 {
    width: 420px !important;
}
body.network-front #eds-picks2 {
    width: 380px !important;
    }
body.keyword-page #content, body.boss-tag .col1 {
    width: 600px !important;
    }
body.boss-tag #content {
    width: 100% !important;
    }
body.keyword-page #related, body.keyword-page #content {
    margin-left: 0 !important;
    }
div.four-col:nth-child(3), body.boss-tag .col {
    width: 480px !important;
    }
#eds-picks-1, #sport-nwf-picks, #on-the-guardian,
 #Multimedia, #most-viewed {
    width: 480px !important;
    }
#box {
    width: 1100px !important;
    }
#content, #main-article-info, #stand-first, #article-header h1, #article-header h2 {
    width: 800px !important;
    margin-right: 40px !important;
    }
#newsticker.large-ticker {
    height: 90px !important;
    }
#related, body.new-grid .two-col, body.article  .two-col {
    width: 260px !important;
    }
.header-feeds .feeds .rss {
    float: right !important;
    margin-top: -40px !important;
    }
body.keyword-page #article-header a.feed {
    font-size: 1px !important;
    padding-top: 20px !important;
    }
.section-heading, #header #guardian-logo {
    margin-bottom: 0 !important;
    }
.section-heading a.feed {
    font-size: 1px !important;
    padding-top: 22px !important;
    }

#weather-header-us, div.two-col:nth-child(4), #wrapper > div:nth-child(4),
#footer-container, #promo, .outbrain-container, #d2-root, #wrapper > span:nth-child(5),
 div.email-subscription-promo:nth-child(2), ul.share-links, .top-navigation,
  #searchbeta, #stayinformed123, .crumb-wrapper, .jobs-box,
  #blahblah, #weath-2col, #small-ad, #Top, #local-info, .feeds-label,
  .standalone-header, #on-this-site, .slot-machine-two.component .ft, .showcase,
  #uswaystostayinformed, .identity-noticebar, #news-direct-media, #stayinformed, #stay-informed,
  #guardianJobsWidget, .guardianFeaturedJobsWidget, .promo-component, #weatherworld, #agagoijla,
  #us-money-widget, #badge-medium, #article-toolbox-side, body.series-index #related, #gasgilhkasg,
  #iyfjhvrystrs, body.blog-index #article-header, .factbox, body.network-front .eight-col, body.boss-tag #related div.col,
  body.network-front div.two-col:nth-child(2), .alt-bookshop-style, .ma-placeholder-related-information-2-horizontal {
    display: none !important;
    }


}
