css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("en.wikipedia.org"), domain("sv.wikipedia.org") {
	body,#searchInputSuggest {
		font-family: georgia,serif !important;
		background: none !important;
	}

	h1.firstHeading {
		margin-bottom: 25px !important;
	}

	#content {
		padding: 3em 4em 3em !important;
		margin: 0 !important;
		border: 0 !important;
	}

	/* Removes article feedback form */
	#mw-articlefeedbackv5,

	/* Removes sister projects table */
	table.metadata.plainlinks.mbox-small, 
    #administrator,
	#anon-banner,
	#anontip,
	#coordinates,
	#donate,
	#featured-star,
	#footer,
	#left-navigation,
	#mw-articlefeedback,
	#mw-head-base,
	#mw-page-base,
	#mw-searchButton,
	#p-coll-print_export,
	#p-interaction,
	#p-logo,
	#p-navigation,
	#p-personal,
	#p-project,
	#p-tb,
	#protected-icon,
	#right-navigation,
	#searchGoButton,
	#siteSub,
	#speak-icon,
	#spoken-icon,
	#toc,
	.ambox,
	.editsection,
	div#siteNotice {
		display: none !important;
	}

	p,ul,dl {
		margin-bottom: 1.7em !important;
	}

	ul {
		margin-bottom: 2.2em !important;
	}

	ul ul {
		margin-bottom: 1em !important;
		margin-top: .8em !important;
	}

	h1 {
		font-size: 300% !important;
		padding-bottom: 10px !important;
		font-weight: bold !important;
		padding-right: 280px !important;
	}

	h2 {
		font-size: 220% !important;
		padding-bottom: 15px !important;
		margin-top: 40px !important;
		font-weight: bold !important;
	}

	#mp-upper h2,#mp-tfp h2 {
		margin-top: 0 !important;
		padding-top: .6em !important;
	}

	h3 {
		font-size: 180% !important;
		padding-bottom: 5px !important;
		margin-top: 40px !important;
	}

	h4 {
		font-size: 150% !important;
		padding-bottom: 5px !important;
		margin-top: 40px !important;
	}

	p,li {
		font-size: 140% !important;
	}

	dd,dt {
		font-size: 110% !important;
	}

	.infobox {
		margin-left: 2.5em !important;
		margin-bottom: 3em !important;
		border-style: solid !important;
		padding: .5em !important;
		width: 200px !important;
		border-radius: 8px !important;
		border-color: #cacaca !important;
	}

	#contentSub,#contentSub2 {
		margin-left: 0 !important;
	}

	div.tleft {
		margin: 0 4em 2em 0 !important;
	}

	div.tright {
		margin: 0 0 2em 4em !important;
	}

	li {
		margin-bottom: 1em !important;
		margin-left: 2em !important;
		line-height: 1.5em;
	}

	li ul li {
		font-size: 100% !important;
		margin-bottom: .6em !important;
	}

	div#catlinks {
		margin-top: 4em !important;
		padding-left: 1em !important;
		padding-right: 1em !important;
	}

	div.dablink {
		padding-left: 0 !important;
	}

	.messagebox {
		margin: 0 0 1em !important;
		text-align: left !important;
		padding: 1em !important;
	}

	dt {
		margin-top: 1em !important;
	}

	h1,h2 {
		border-bottom-style: dotted !important;
	}

	#content blockquote,table.cquote {
		margin-left: 4em !important;
		margin-right: 4em !important;
		margin-bottom: 2em !important;
		line-height: 2.2em !important;
		background: #efefef !important;
		padding: 1.4em 2em 0.1em !important;
		border-radius: 10px !important;
	}

	table.cquote {
		font-size: 1.4em !important;
		line-height: 1.4em !important;
		padding: 1.4em .8em !important;
		border-collapse: separate !important;
	}

	table.cquote p {
		font-size: 1em !important;
	}

	.catlinks {
		border-radius: 5px !important;
	}

	div.thumbinner {
		border-radius: 5px !important;
		padding: 5px !important;
	}

	.catlinks {
		border-radius: 5px !important;
	}

	div.thumbinner {
		border-radius: 5px !important;
		padding: 5px !important;
	}

	table#mp-topbanner {
		margin-top: 6em !important;
	}

	#p-lang {
		position: relative;
		top: 0;
		overflow: visible !important;
		bottom: 400px;
		left: 1300px;
		margin-top: -150px !important;
	}
	#p-lang h3 {
		background: none !important;
		margin: 0 !important;
		padding: 0 !important;
	}
	#p-lang h3 a {
		margin: 0 !important;
		padding:  0 !important;
	}
}
