css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://8tracks.com") {
    
    /* Remove comments */
    #mix_comments,
        
    /* Remove stats */
    .mix-stats,
    
    /* Remove ads */
    .seo_text,
    #iphone_promo_form,
    #eighttracks_message,
    #sidebar_ad_wrapper,
    .favicon_lastfm,
    #seo_text,
    .js-connected-sites,
    
    /* Remove zoom button */
    #zoom,
    
    /* Remove rss */
    .rss,
    
    /* Remove sidebar */
    #sidebar,
    .sidebar,
    
    /* Remove mix sidebar */
    #mix_interactions,
    
    /* Remove favorite buttons */
    .i-favorite,
    .mix_like,
    .quick_add,
    .quick_actions .pipe,
    #share_user,
    
    /* Remove itunes link */
    .amazon .pipe,
    .buy,
    
    /* Remove follow button*/
    .follow,
    
    /* Remove log in / sign up buttons */
    .login,
    #signup_link,
    #auth_nav div
    {
        display: none !important;
    }

    
    /* Remove footer */
    #footer {
        z-index: -10 !important;
        position: absolute !important;
        top: 0 !important;
        left: -2000px !important;
    }

    /* Center 8tracks */
    #content {
        margin: 0 auto !important;
        float: none !important;
    }
    
    /* Fix space between player and playlist */
    #mix_wrapper {
        width: 625px !important;
        margin-bottom: 130px !important;
    }
    
    /* Fix mix image */
    #play_area, #mix_details {
        width: 625px !important;
    }
    
    /* Remove arrow */
    .bubble.arrow:before,.bubble.arrow:after {
        transform: none !important;
        background: none !important;
        box-shadow: none !important;
    }
    
    /* Fix pagenation */
    .seo_pagination {
        height: 100px !important;
    }

    /* Remove border on root */ 
    .split {
        border: none !important;
    }
    
    .mix_art_wrapper {
        position: relative!important;
        margin: 0 auto !important;
    }
    
    #mix_name {
        position: absolute !important;
        top: -500px;
    }
    
    #mix_details {
        height: auto !important;
    }
    
    #play_overlay {
        bottom: 50px !important;
    }
}
