css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("telegraph.co.uk") {

body {
    color: #585858 !important;
    }

.twoThirdsThird2 > div:nth-child(2), .oneSixth,
 #tmglFooterLarge, #searchBar, .storyFt, div.twoThirds:nth-child(6),
  #tmg-related-links, .puff, .tabs > ul:nth-child(1) {
    display: none !important;
    }

#tmglSite {
    width: 1100px !important;
    }
.twoThirdsThird, #tmglBody, .newPortalWrapper, .twoThirds,
div.twoThirdsThird:nth-child(2) > div:nth-child(3) {
    width: 1100px !important;
    }

.oneThird {
    width: 100% !important;
    }
.fiveTwelfths, div.twoThirdsThird:nth-child(4) > div:nth-child(2) {
    width: 66% !important;
    }
.oneHalf {
    width: 66% !important;
    }
div.borderTSixths:nth-child(2), div.oneThird:nth-child(4) {
    width: 49% !important;
    }
.oneQuarter {
    width: 320px !important;
    }
.opta-widget-container {
    width: 500px;
}
.iefix > div:nth-child(1) > h1:nth-child(1),
#mostviewed .tabs .view-content li h3, .summarySmall h3, .opta-widget-container tbody td,
.opta-widget-container thead th   {
    font-size: 2.2em  !important;
    }
 .excerpt {
    font-size: 1.5em !important;
    }
.summary p, .iefix > div:nth-child(1) > h2:nth-child(2),
 .headlineOnly, .labelOnSec, #dsq-comments {
    font-size: 1.8em  !important;
    color: #585858 !important;
    }
.labelAbstract a, #tmglPageTitle h2 p, div.headlineImageLeft h3 a,
.summaryMediumLarge div h3 a, .summary h3 a,
.summaryMedium h2 a, .opta-widget-container h2 span {
    font-size: 1.3em !important;
    line-height: 1.3 !important;
    }
.twoThirdsThird2, .twoThirdsd, .borderTwoThirds,
.borderOneThird, .threeThirdsSolid, .threeThirds, .twoThirdsSplit {
    background: none !important;
    }

.story, .label.entReview h3, .summary ul li {font-size: 1.8em !important;}
p {line-height: 1.2 !important;}

#storyEmbSlide .slideshow .ssImg .artImageExtras .caption {
    color:#585858 !important;
    font-size: 1.5em !important;
    line-height: 1.38em;
    margin: 0px 5px 0px 0px;
    }

.summary .piccentre {
    margin-right: 150px !important;
    }
.containerdiv {
    float: none !important;
    }
.carv2Gallery, .carouselGallery {
    margin-left: 120px !important;
    
    }
.carouselGallery {
    overflow: visible !important;
    }
}
