css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com") {
DIV#sponsorsModule_part.sideAreaModule,
DIV#flickrModule_part.sideAreaModule,
DIV#youtubeModule_part.sideAreaModule,
DIV#tweetsModule_part.sideAreaModule,
DIV#fusionModule_part.sideAreaModule,
DIV#mobileModule_part.sideAreaModule,
DIV#portfolioModule_part.sideAreaModule,
DIV#newsfeedModule_part.sideAreaModule,
DIV#timelineModule_part.sideAreaModule
	{display: none !important;}
/* ======================== MAIN AREA BEGIN ======================== */
#feedlyFrame {
	width: auto !important;}

#feedlyPage {
	width: auto !important;}

#feedlyPart {
	width: 100% !important;
	box-sizing: border-box !important; /* Saving sanity using border-box*/}

#mainArea {
	width: auto !important;}
/* ======================== MAIN AREA END ======================== */
/* ======================== TITLE ONLY VIEW BEGIN ======================== */
/* I've changed nothing here */
/* ======================== TITLE ONLY VIEW END ======================== */
/* ======================== MAGAZINE VIEW BEGIN ======================== */
.u4Entry {
	max-width: 900px !important; /* This is for readability, very long lines are hard to read */
	margin-left: auto !important;
	margin-right: auto !important;
	}
#recommentationAreaHeadlines {
	width: 900px !important;
	margin-left: auto !important;
	margin-right: auto !important;
	}
/* ======================== MAGAZINE VIEW END ======================== */
/* ======================== CARDS VIEW BEGIN ======================== */

/* ======================== CARDS VIEW END ======================== */
/* ======================== FULL ARTICLE VIEW BEGIN ======================== */
.entryBody {
	max-width: none !important;}
#sideArea {
	display: none !important;}

.entryBody iframe { /* YOUTUBE VIDEOS WIDER HERE */
	margin: 10px 10px 10px 0px !important;
	width: 100% !important;
	height: 480px !important;
	margin-left: auto !important;
	margin-right: auto !important;
	}
.entryBody table, .entryBody td {
	max-width: none !important;
	}
.content img {
	max-width: 100% !important;
	width: auto !important;
	height: auto !important;
	margin-left: auto !important;
	margin-right: auto !important;
	}
.content p, .content li, .content ul, .content h1, .content h2, .content h3, .content h4, .content h5, .metadata, .entryTitle, .u100Entry .title {
	max-width: 900px !important;
	width: auto !important;
	height: auto !important;
	margin-left: auto !important;
	margin-right: auto !important;
	}

.content, p, ul, h1, h2, h3, h4, h5 {
	max-width: none !important;
	margin-left: auto !important;
	margin-right: auto !important;
	}
#mainBar {width: auto !important;
	}
.u100Entry {
	max-width: none !important;
}

/* ======================== FULL ARTICLE VIEW END ======================== */
}
