css:
/* Stylesheet for linux.org.ru by pda2@yandex.ru (1.2; 24.08.2013) - White2 Classic Theme */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("https?://((www\\.)?linux\\.org\\.ru/|opensource\\.ru)(?!wiki)(.*)")
{
  body{ font-family: "DejaVu Serif", "Droid Serif", Georgia, "Times New Roman", serif !important; }
  textarea { font-family: "DejaVu Serif", "Droid Serif", Georgia, "Times New Roman", serif !important; }
  code { font-family: Consolas, "DejaVu Sans Mono", "Droid Sans Mono", "Courier New", monospace, sans-serif !important; }
  a { color: #0033ee !important; }
  a:hover { color: #03e !important; }
  a:visited { color: #03a !important; }
  #hd { background-color: #ffffff !important; }
  a#sitetitle { color: black !important; }
  div.boxlet { background-color: #e7e7e7 !important; }
  div.boxlet h2 { background-color: #36e !important; }
  div.boxlet h2 a { color: white !important; }
  article h2 a { color: black !important; }
  article h2 a:hover { color: black !important; }
  article h2 a:visited { color: black!important; }
  .nav{ border: gray 1px solid !important; background: #f0f0f0 !important; }
  header h1[itemprop="headline"] { color: black !important; }
  header h1[itemprop="headline"] a { color: black !important; }
  header h1[itemprop="headline"] a:hover { color: black !important; }
  header h1[itemprop="headline"] a:visited { color: black !important; }
  article[itemprop="comment"] div.title { background-color: #90ee90 !important; }
  article[itemprop="comment"] div.sign { font-style: italic !important; }
  .message-table { background: transparent !important; border-collapse: collapse; }
  .message-table thead { background: #f0f0f0 !important; }
  .message-table tbody { background: transparent !important; }
  .message-table tbody tr { border-bottom: 1px solid #f0f0f0 !important; }
  .message-table tbody tr:hover{ background: #eee !important; }
  .message-table tfoot { background: #f0f0f0 !important; }
  .stars { color: #32cd32 !important; font-style: normal !important; }
  .btn:hover,#bd .btn:focus{background-color:#03e !important; border:1px solid #03e !important;}
}
