css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ustream.tv") {
.topLeaderBoardContainer
 {display: none !important;}

body {background: none !important;}

/*--- header ---*/
#Header {position: static !important; top: inherit !important; left: inherit !important;}
.main-content, .content {width: auto !important;}

/*--- left columns (video player view) ---*/
.explore #ViewWrapper {
 display: block !important;
 position: static !important;
 left: inherit !important;
 top: inherit !important;
 float: left;
 margin: 0 !important;
 }
.explore .viewer .info {min-height: inherit !important;}
.explore .viewer .info .title {
 width: 100% !important; min-height: inherit !important;
 padding: 0 !important;
 background-color: white;
 line-height: inherit !important;
 }
.viewer .info .title h2 {height: auto !important; margin: 0 !important;}
.explore .leftSquareContainer {display: none !important;}

/*--- right columns (main) ---*/
#MainWrapper {width: auto !important; margin: 0 !important;}
.explore #ContentWrapper {width: auto !important;}
.bottomLeaderBoardContainer {display: none !important;}
.topLeaderBoardContainer {width: auto !important;}
#DiscoExp {width: auto !important; margin: 0 0 0 324px !important;}
#DiscoExp>div {width: 100% !important;}
.demSub {
 height: auto !important;
 padding: 0 !important;
  white-space: normal !important;
 }
.demSub a {padding: 0 !important; line-height: inherit !important;}
.deContentWrap {min-height: inherit !important;}
.deContentWrap.noSidebar .deMainCol,
.deContentWrap.noSidebar .deMainCol div.horizWrap > ul
 {width: auto !important;}
.deContentWrap.noSidebar .deMainCol .horizWrap {width: auto !important;}
.deMainCol div.horizWrap > ul, .deMainCol div.horizWrap > ul.new > .loading,
.deMainCol div.horizWrap > ul > .initial.loading
 {min-height: inherit !important;}
.deMainCol div > ul li.upcomingFeatured .featuredContent {width: auto !important;}
.deContentWrap.noSidebar .pageScroller .scrollPagerWrp {width: auto !important;}
}
