css:
@-moz-document url-prefix("http://inoreader.com/"), url-prefix("https://inoreader.com/"), url-prefix("https://www.inoreader.com/"), url-prefix("http://beta.inoreader.com/"), url-prefix("https://beta.inoreader.com/"), url-prefix("http://www.inoreader.com/") {
body {-webkit-font-smoothing:auto !important;}
#footer {display:none;}
#subscriptions_buttons {border-bottom:1px solid #eee;box-shadow:0 0 0px !important;background:white !important;}
#subscriptions_buttons button {background:white !important;border-color:#ccc;}
.article_current {border-left: 1px solid #16c !important;border-right:1px solid #16c !important;}
.article_card.article_unseen {border-left: 1px solid #ccc !important;}
.article_card.article_unreaded {border-left:1px solid #ccc !important;}
.article_footer {background-color:#fff;margin:5px 0px 0px 5px !important;}
.article_title {font-weight:bold !important;font-size:130%;max-width:650px;}
.article_author {font-weight:normal !important;}
.article_content {line-height:120% !important;font-size:13px !important;}
.article_footer button img {-webkit-filter: grayscale(100%) !important;}
.article_footer button:hover img {-webkit-filter: grayscale(0%) !important;}
span.tree_unseen {color:black !important;}
#tree .parent_div {margin-top:3px;}
#parent_channel {margin-top:-5px;margin-bottom:5px;}
input {font-family:Arial, Helvetica;box-shadow:none;border-radius:2px;}
#searcher {margin-top:10px;margin-bottom:10px;}
#searcher input {padding:10px;}
#tree_pane, #reader_pane {padding-bottom:21px !important;}
#tree_pane {background-color:white !important;}
div#subscriptions_nav img {display:none;}
.parent_div_inner img {display:inline !important;}
legend {padding-left:5px;}
div#no_more_div {background-color:#eee !important;height:75px !important;line-height:75px !important;color:#666;font-style:italic;}
.comments_body {background-color:white;font-size:120%;font-family:Helvetica, Arial, sans-serif;}
.comments_title {background-color:white;font-size:120%;font-family:Helvetica, Arial, sans-serif;}
.user_comments.smallradius {box-shadow:none !important;padding:10px;border:1px solid #eee;}
.ar {margin-bottom:10px !important;}
}
