css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.baka-tsuki.org/project/"),
               url-prefix("http://baka-tsuki.org/project/"),
               url-prefix("https://www.baka-tsuki.org/project/"),
               url-prefix("https://baka-tsuki.org/project/"){
   body {
      background: #111 fixed no-repeat !important;
   }
   .pBody {
      background-color: #999999 !important;
      border-color: #FFFFFF !important;
   }
   #mw-page-base {
      background: transparent !important;
   }
   #left-navigation > .vectorTabs,
   #right-navigation > .vectorTabs {
      border-left: 1px white solid !important;
      border-top: 1px white solid !important;
   }
   .vectorTabs li {
      border-bottom: 1px rgb(167, 215, 249) solid !important;
   }
   .vectorTabs li.selected {
      border-bottom: none !important;
   }
   #content {
      background-color: #000000 ! important;
      background-image: url('data:image/png;base64,') !important; 
      background-repeat: repeat-x !important;
      background-attachment: fixed !important;
      color: white !important;
   }
   table {
      background-color: transparent !important;
      color: white !important;
   }
   #toc, .toc, .mw-warning {
      background-color: #333333 !important;
   }
   h1,h2,h3,h4,h5 {
      color: white !important;
   }
   div.tright {
      border-color: transparent !important;
   }
   div.tright > div {
      background-color: #333333 !important;
   }
   .thumbcaption {
      background-color: #000000 !important;
   }
   blockquote {
      background-color: transparent !important;
   }
   *[class*="ns-"] dl {
      background-color: #222222 !important;
   }
   #mw-head.noprint {
      background-color: #000000 !important;
   }
   div#mw-content-text {
      background-color: transparent !important;
   }
   div.vectorTabs,
   div.vectorTabs ul,
   div.vectorTabs ul li {
      background-image: none !important;
      background-color: #333333 !important;
   }
   div.vectorTabs li.selected a {
      color: #CCCCCC !important;
   }
   div#mw-content-text > p:first-child + div {
      background-color: royalblue !important;
      color: white !important;
       border: 1px solid gray;
   }
   a:link {
      color: #33B5E5 !important;
   }
   a:visited {
      color: #0385B5 !important;
   }
   a.new {
      color: #AA0000 !important;
   }
}
