css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("badoo.com") {

    body, #head{
        background-image:none !important;
        
        border: hsla(211, 60%, 40%, 1) !important;
        background-image: linear-gradient(to top, hsla(211, 50%, 60%, 0.5), hsla(211, 50%, 60%, 1)) !important;
	
    }
    
    #sp_wrap,
    .rob > a:nth-child(4), 
    #customizationWrapper, 
    div.cont:nth-child(3),
    div.rob-cr,
    .rtc_spotlight,
    h3.rtc_ec,
    #ready_to_chat,
    .is_special_label,
    .is_special_icon,
    #is_special,
    .form_title,
    a.im_uaction:nth-child(3),
    .ft_at
    {display: none !important}
    
    
    #userlist {
        bottom: -120px !important;
    }
    
    #userlist > *,#interlocutor > div:nth-child(1),
    .ma_h, #messages, #type > div:nth-child(1),
    #mm_cc{
        background-image: linear-gradient(to bottom, hsla(211, 60%, 40%, 1) 30%, hsla(211, 60%, 40%, 0.4) 50%, hsla(211, 60%, 40%, 1) 70%) !important;
    }
    
    #messages{bottom: -100px !important;}
    
    .chat_msg_caller {
        padding: 1px 0px 1px 50px !important;
        border-color: hsla(91, 60%, 60%, 1) !important;
        background-color: hsla(91, 60%, 60%, 0.6) !important;
        border-radius: 2em;
    }
    
    .chat_msg_owner {
        padding: 1px 0px 1px 5px !important;
        border-color: hsla(181, 60%, 60%, 1) !important;
        background-color: hsla(181, 60%, 60%, 0.6) !important;
        border-radius: 2em;
    }
    
    #f{
        position: absolute !important;
        bottom: 0px !important;
        left: 10px !important;
        width: 600px !important;
    }
    

}
