css:
@-moz-document domain(feedly.com) {

/* Feedly*/
.condensed .inlineFrame {
    margin: 0px !important;
    padding-left: 40px !important;
    padding-right: 30px !important;
}

.entryBody {
    max-width: 100% !important;
    font-size: 16px !important;
}

#box, #feedlyPage, #feedlyPart {
    width: 99.7% !important;
}

#feedlyFrame {
    margin-left: 228px !important;
}

#mainBar {
    margin-left: 0px !important;
    margin-left: 0px !important;
    width: 100% !important;
}

h2 {
    margin-top: 0px !important;
}

.entryBody h3 {
    font-size: 20px !important;
}

.condensed .entryholder .u100Entry {
    max-width: 100% !important;
}

.pin , [style].popup {
    display: block !important;
    opacity: 0 !important;
    visibility: hidden;
    -webkit-user-select: none !important;
}

div#feedlyPart0.aera {
    padding: 23px !important;
}

div#section0.section {
    height: 10px !important;
/*display:none !important;*/;
}

#feedlyMessageBar {
    display: none !important;
}

#feedlyPageHeader {
    margin-left: 20px !important;
}

#feedlyPart {
    padding-right: 8px !important;
}

#floatingBar {
    margin-left: 228px !important;
    padding-top: 8px !important;
    width: 88.2% !important;
}

.u4Entry {
    max-width: 97% !important;
}

div.u100Entry {
    max-width:100% !important;
}

#floatingTitleBar {
    padding-left: 8px !important;
}

.condensed .entryholder .u100entry {
    margin-left: 0px !important;
}

div.button.primary.small {
    background-color: rgba(255, 102, 0, 0) !important;
}

.sliderWidth {
  width: 80% !important;
}

.button.primary {
    color: #F0F0F0 !important;
}
/* image size */
.inlineFrame .entryBody img {
    border: 0px;
    max-width: 90% !important;
    width: auto !important;
    height: auto !important;
    max-height: 800px !important;
}
img.pinable {
    width:100% !important;
    margin: auto !important;
    max-height: 100% !important;
    margin-top: 0rem !important;
    margin-bottom: 0rem !important;

}
/*嵌入*/
iframe{
    margin:auto !important;
}

/*左側欄位*/
div#navSelector_store.navSelector {
    margin-right: 0px !important;
    width: 122px !important;
}

#navSelector_my {
    margin-left: 0px !important;
    padding-left: 20px !important;
    width: 78px !important;
}

#feedlyTabs {
    width: 208px !important;
    padding-right: 10px !important;
    padding-left: 2px !important;
}

#feedlyTabsHolder {
    width: 224px !important;
}

div#feedlyTabs > div {
    margin-top: 8px !important;
}

#mytab ,#savedtab {
    padding-left: 8px;
}

#feedlyPart0.area {
    padding-left: 8px !important;
}
/*內容*/
.entrybody {
    font-size: 1.04em !important;
}

.inlineFrame .entryBody img {
    margin: 0px auto;
}

.title {
    max-width: 100% !important;
    margin-bottom: 8px !important;
}

.frameActionsTop {
    height: 13px !important;
}
	/* code ends */

}
