@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.xda-developers.com") {

.post-options-retweet, .flag, .author-stats, .donate, .retweet-threads, #topiconbox, #topcontainbox, #forum-jump, #footer-credits, #stats, #qrform, #pagination, #info-footer, #footer-nav, .share, #footer_social, #xda_header_social, tr[id="xda_header_latest"], a[class="postTwitterIcon"], a[class="postFacebookIcon fbs_post"], a[class="headerGPlusIcon"], a[class="moreInfoButton btn-silver"], .moreUserInfo, img[src^="images/smilies/"], img[src^="http://media.xda-developers.com/images/icons/"] {display:none!important;}
/*, div[style^="display:"]*/

.forumdisplay-footer {
height: 30px!important;
}

#xda_global_wrapper, .lastPostDetails, .tborder, #stats, #ad-billboard-bottom {min-width:200px!important;}
#page_rightPane, #postLegend, .postmenu_profile {width:100px!important;}
