css:
.wtf-module,
.trends,
.site-footer,
.profile-summary,
.profile-nav,
.enhanced-media-thumbnails,
.js-mini-profile-stats-container,
.home-container .content-header .header-inner,
.home-container .content-no-header .no-header-inner,
.wrapper-profile .content-header,
.wrapper-connect .content-header,
.wrapper-discover .content-header {
  display: none !important;
}

.wrapper,
.wrapper-narrow,
.wrapper-permalink {
  width: 540px;
}

.content-main {
  position: relative;
  top: -2px;
}
.wrapper-connect .content-main,
.wrapper-discover .content-main {
  top: -11px;
}

.new-tweets-bar {
  top: 0;
  border-top: none;
}

.home-container .content-header,
.home-container .content-no-header {
  border: none !important;
}

.mini-profile {
  margin: 0;
}

.tweet-user {
  border-radius: 5px 5px 0 0 !important;
  border-bottom: 1px solid #DDD;
}

.wrapper-home .content-header .header-inner,
.wrapper-home .content-no-header .no-header-inner {
  display: none;
}

.dashboard .tweet-content .tweet-box,
.condensed .twitter-anywhere-tweet-box-editor,
.tweet-box .twitter-anywhere-tweet-box-editor {
  width: 477px !important;
}
.twttr-dialog .condensed .twitter-anywhere-tweet-box-editor,
.twttr-dialog .tweet-box .twitter-anywhere-tweet-box-editor {
  width: 452px !important;
}

.dashboard,
.content-main {
  float: none !important;
  width: 520px;
  margin: 0 auto;
  border-radius: 0;
}

.profile-card-inner {
  width: 48%;
}

.profile-card.profile-header {
  float: none;
  margin: 0 auto;
}

.stream-item,
.unfocusable-stream-item,
.timeline-end .has-items .has-more-items,
.stream-loading,
.mini-profile,
.module,
.tweet-box .tweet-user {
  border: none !important;
}

.stream-container {
  border-top: 1px solid #e8e8e8;
}

.module ul:last-child li:last-child .list-link,
.module ul:last-child li.last-item .list-link,
.module .flex-module:last-child,
.module .contrast-flex-module:last-child {
  border-radius: 0;
}
