css:
@-moz-document domain(tumblr.com) {
#home_button {

    padding-right: 10px !important;
    }


#home_button a {

    height: 0 !important;
    width: 0 !important;
    top: -4px !important;
    padding-left: 37px !important;
    padding-top: 37px !important;
    background: url('http://25.media.tumblr.com/tumblr_ma2g011lwg1qj6h17o1_100.png') !important;
    }


#home_button .tab_notice {
    left: 45px !important;
    right: auto !important;
    }


#logo {
    height: 0 !important;
    width: 0 !important;
    padding-left: 310px !important;
    padding-top: 85px !important;
    background: url(http://i43.tinypic.com/ws9z42.jpg) no-repeat !important;
    }

body {
    cursor: url("http://i889.photobucket.com/albums/ac98/rukanyan/exocursor.png"), default !important
    }

#dashboard_index,
 #dashboard_inbox,
#dashboard_edit_post,
#dashboard_submissions,
#dashboard_drafts,
#dashboard_post_queue,
#dashboard_likes,
#dashboard_followers,
#dashboard_following,
#dashboard_directory,
#dashboard_lookup_contacts,
#main_doc_page,
#dashboard_settings,
#dashboard_create_tumblelog,
#dashboard_members,
#main_tag,
#main_meetups,
#main_about,
#main_explore,
#dashboard_goodies,
#main_developers,
#main_themes,
#dashboard_manage_themes,
#main_edit_theme,
#main_jobs,
#main_policy,
#dashboard_tumblelog_settings {
background:#a8a8a8 url(http://static.tumblr.com/9uintcb/B0nmp4e6y/hell_yeah_i_finally_did_it_ahahaa.png) no-repeat bottom right fixed! important 
}
#dashboard_index div.l-content,
#dashboard_inbox div.l-content,
#dashboard_submissions div.l-content,
#dashboard_drafts div.l-content,
#dashboard_post_queue div.l-content,
#dashboard_likes div.l-content,
#dashboard_followers div.l-content,
#dashboard_members div.l-content,
#main_tag div.l-content,
#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(0,0,0,0.1) !important
 }
.selection_nipple { 
border-bottom-color: rgba(0,0,0,0.1) !important 
}
.permalink { background: none !important 
}
.avatar_frame {
background-color: rgba(255,255,255,0.1) !important; 
border-color: rgba(255,255,255,0.1) !important 
}
.notification {
background: rgba(0,0,0,0.1) !important;
border: rgba(255,255,255,0.1) !important;
background-image: none !important;
margin-bottom: 1px !important
 }
.notification a { 
border-bottom: 1px solid rgba(0,0,0,0.1) !important 
}
.notification a:hover { 
border-bottom: 1px solid rgba(0,0,0,0.3) !important 
}
.notification_type_icon { 
display: none !important
 }
.nipple { 
border-right-color: rgba(0,0,0,0.07) !important
}
.controls_section li { 
background: rgba(0,0,0,0.1) !important
 }
.with_subtitle small { 
color: rgba(0,0,0,0.5) !important 
}
.recessed a { 
color: rgba(255,255,255,0.7) !important 
}
.radar { 
display: none !important 
}
* { text-shadow: none !important }
h1, .title { 
color: rgba(0,0,0,0.6) !important 
}
.no_posts_found, h1.dashboard_header { 
color: rgba(255,255,255,0.9) !important 
}
.dashboard_options_form {
background: rgba(0,0,0,0.1) !important;
border: none !important;
color: rgba(255,255,255,0.7) !important 
}
.dashboard_options_form_disclaimer, .dashboard_options_form_disclaimer a { 
color: rgba(255,255,255,0.9) !important 
}
.blue_note, .blue_note a {
background: rgba(0,0,0,0.2) !important;
color: rgba(0,0,0,0.6) !important 
}
#footer, #footer a {
border: none !important;
color: rgba(0,0,0,0.5) !important 
}
img[src*="http://static.tumblr.com/duegx7i/oKGm04vmm/000.png"] {
opacity: 0;
visibility: hidden 
}
.new_post {
background: #fff url(http://static.tumblr.com/duegx7i/oKGm04vmm/000.png) no-repeat center bottom 
}
.new_post_label {
-webkit-text-stroke:0 !important;
color: rgba(0,0,0,0.4) !important;
opacity:1 !important;
top: 0px !important;
padding-top: 80px
 }
.new_post_label:hover {
 color: rgba(0,0,0,0.7) !important 
}
#pagination a {
background: rgba(0,0,0,0.4) !important;
color: rgba(255,255,255,0.7) !important
 }
#pagination a:hover {
color: rgba(255,255,255,0.9) !important 
}
#dashboard_controls a, #dashboard_controls .control {
color: rgba(255,255,255,0.7) !important 
}
.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {
display: none !important 
}
}
