css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.youtube.com"), url-prefix("https://www.youtube.com") {

/*Cleanup*/
#ticker.ytg-base,
#yt-masthead-dropdown,
div#my-subscriptions-promo,
div.branded-page-v2-secondary-col,
hr.branded-page-v2-col-container-bottom-border,
#footer-container,
#footer-hh-container,
div#yt-uix-clickcard-card1,
.content-region,
.yt-uix-clickcard-card-body,
.yt-uix-card-body-arrow-vertical,
.yt-uix-card-border-arrow-vertical,
#watch7-branded-banner,
#watch7-playlist-tray-container,
#player-branded-banner {
    display: none !important
}

div#player {
    background: none !important
}

.ytcenter-align {
   background: none !important
}

#player-api {
    height:720px !important; 
    width:1272px !important;
    margin-left: -215px !important
}

#watch7-sidebar {
    background-color: white !important;
    margin-top: 0px !important
}

#watch7-main {
    margin-left: -115px !important;
    margin-top: 4px !important;
}

#guide-container {
    /*position: fixed !important;
    margin-top: 50px !important;
    left: 10px !important;
    color: gray !important;*/
    display: none !important
}

#page-container {
   width: 1300px !important;
   margin-top: 4px !important;
   margin-right: auto !important;
   margin-left: auto !important;

}

#yt-masthead-container > #yt-masthead {
   display: table !important;
   width: 1120px !important;
   margin-right: auto !important;
   margin-left: auto !important;
}

#watch7-video-container, #watch7-main-container, #body-container, html {
    background-color: #1b1b1b !important
    }
    
#yt-masthead-container {
    background-color: white !important;
}

#content {
    margin-bottom: -300px !important;
    background-color: #1b1b1b !important
}

span.title {
    color: black !important;
}

button, a.yt-uix-button.start.link-gplus-lightbox.yt-uix-sessionlink.yt-uix-button-hh-default, span.yt-badge-std {
    border-color: transparent !important;
    background: transparent !important;
    color: gray !important;
    text-shadow: none !important;

}


/*Youtube Center*/
div#header div {
    background-color: white !important;
}

}
