css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

html, body  {
    background: #606060 !important;
    background-image: url("http://i.imgur.com/8i6ATGr.png") !important;
    color:      #cccccc !important;
    font-family: DejaVu Sans, sans !important;
    /* max-width: 800px !important; */
    margin: 0px auto !important;
    padding: 0;
}
a {
    color: #f27e30 !important;
}
a:visited {
    color: #b2a24e !important;
}

div {
    /* max-width: 800px !important; */
    border-color: #101010 !important;
}
div#qrHeader.drag.postblock {
    background: #8d59b2 !important;
}
div.pagelist.desktop.depagerEnabled, div#threadWatcher.extPanel.reply {
    background: #333 !important;
    border: 1px solid #222 !important;
    box-shadow: 0 0 3px 3px #0a0a0a;
    padding: 5px !important;
}
div.post.reply {
    background: #252525 !important;
    border: 2px solid #1d1d1d !important;
    border-radius: 6px;
    margin: 10px !important;
    box-shadow: 0 0 2px 2px #1d1d1d !important;
    background-image: url("http://i.imgur.com/MlpWueu.png") !important;

}
div.extPanel.reply {
    background: #303030 !important;
}
div.sideArrows {
    color: #8d59b2 !important;
}
div.thread {
    background: #2a2a2a !important;
    border: 1px solid #101010 !important;
    border-radius: 6px !important;
    background-image: url("http://i.imgur.com/k8bOJJ5.png") !important;
    padding: 3px;
}

li {
    border-color: #101010 !important;
}
span.dateTime {
    color: #bb1000 !important;
    font-family: DejaVu Sans Mono, monospace !important;
    font-size: 13px !important; 
}
span.fileText {
    font-family: DejaVu Sans Mono, monospace !important;
    font-size: 13px !important; 
}
span.name  {
    color: #528fcc !important; 
    font-family: DejaVu Sans Mono, monospace !important;
    font-size: 13px !important;    
}
span.postNum a, span.postNum.desktop a, a.quotelink{
    color: #66bf39 !important;
    font-family: DejaVu Sans Mono, monospace !important;
    font-size: 13px !important; 
}
span.postertrip {
    color: #406080 !important;
    font-family: DejaVu Sans Mono, monospace !important;
    font-size: 13px !important; 
}
span.quote {
    background: #52992e !important;
    color: #202020 !important;
    /* max-width: 600px !important; */
    margin: 0px auto !important;
    white-space: pre-wrap !important;
}
span.subject {
    color: #74d941 !important;
    font-family: DejaVu Sans Mono, monospace !important;
    font-size: 13px !important; 
}


strong.capcode.hand.id_mod {
    color: #ff0000 !important;
}



textarea, input {
    background: #202020 !important;
    color: #a0a0a0 !important;
    font-family: DejaVu Sans Monospace, monospace !important;
}

table.postForm {
    padding: 10px !important;
    border: 1px solid #101010 !important;
    background: #202020 !important;
}

table.postForm tr td {
    background: #404040 !important;
    color: #a0a0a0 !important;
    border-color: #000000 !important;
}

hr {
    border-color: #8d59b2 !important;
}

/* "hover" background */
.reply:target, .reply.highlight {
    color: #ffffff !important;
    font-size: 18px !important;
}



}
