@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.google."), url-prefix("http://www.google."), domain("google.com"), domain("google.fr"), domain("google.co.uk") {
body {overflow-x:hidden;}
body, a, .gbqfba, *{font-family:Roboto, sans-serif !important;}
#gbzw {margin:0 !important;}
#gbz, #gbz.gbes {background:#303030;left:0 !important;top:-80px !important;transition:all 0.3s !important;width:100% !important;height:100px !important;}
#gbzw {width:100% !important;}
#gbz:hover {top:0 !important;}
#gbmm, #gbd {font-size:10pt !important;width:auto !important;}
#gbz:after {content:"...";pointer-events:none !important;font-weight:bold;font-size:20pt;color:#FFF;bottom:3px !important;position:absolute !important;width:100% !important;text-align:center !important;}
#gbx3 {pointer-events:none !important;}
#gbx3 {display:none;}
#gbzc .gbt {border-radius:2px !important;padding:0 !important;height:64px !important;width:64px !important;margin-top:10px !important;margin-right:20px !important;background-position:center !important;font-size:0 !important;}
.gbzt, .gbzt .gbts {padding:0 !important;height:100% !important;width:100% !important;display:inline-block !important;background-position:center !important;}
#gb_1 .gbts {background-image:url(http://www.google.com/images/icons/product/search-64.png) !important;}
#gb_2 .gbts {background-image:url(http://www.google.com/images/icons/product/images-64.png) !important;}
#gb_8 .gbts {background-image:url(http://www.google.com/images/icons/product/maps-64.png) !important;}
#gb_78 .gbts {background-image:url(http://www.google.com/images/icons/product/play-64.png) !important;}
#gb_36 .gbts {background-image:url(http://www.google.com/images/icons/product/youtube-64.png) !important;}
#gb_5 .gbts {background-image:url(http://www.google.com/images/icons/product/news-64.png) !important;}
#gb_23 .gbts {background-image:url(http://www.google.com/images/icons/product/googlemail-64.png) !important;}
#gb_25 .gbts {background-image:url(http://www.google.com/images/icons/product/drive-64.png) !important;}
#gb_24 .gbts {background-image:url(http://www.google.com/images/icons/product/calendar-64.png) !important;}
#gb_119 .gbts {background-image:url(http://www.google.com/images/icons/product/gplus-64.png) !important;}
#gb_51 .gbts {background-image:url(http://www.google.com/images/icons/product/translate-64.png) !important;}
.gbzt.gbp1 {background-color:rgba(100,100,100,0.8) !important;border:none !important;}
.gbtb2 {border:none !important;}
.gbt .gbma {border-width:20px !important;margin-top:20px !important}
#gbztms {height: 56px !important;}
#gbd {
    margin-top: 30px !important;
    
}
#gbzc .gbt:first-child {margin-left:20px !important;}
#gbzw .gbto #gbs {top: 61px !important;}
#gbqfqw, .signin-box input[type="email"], .signin-box input[type="text"], .signin-box input[type="password"] {border:1px solid #E9E9E9 !important;border-radius:2px !important;color:#555 !important;}
#gbx1 {top:20px !important;}
#gbq, #gbu {top:20px !important;}
#gbqfqw.gsfe_a,.signin-box input[type="email"]:hover, .signin-box input[type="text"]:hover, .signin-box input[type="password"]:hover {border-color:#DCDCDC !important;}
#gbqfqw.gsfe_b, .signin-box input[type="email"]:focus, .signin-box input[type="text"]:focus, .signin-box input[type="password"]:focus {border-color:#4d90fe !important;}
.gssb_e {border:1px solid #E9E9E9 !important;box-shadow:none !important;border-radius:2px !important;}

@-moz-document domain("accounts.google.com") {
/* Sign in */
body {background-size:cover;}
#signIn {border-radius:0 !important;}
.product-info, .google-footer-bar {display:none !important;}
.google-header-bar {background:none !important;border:none !important;}
.sign-in {float:none !important;margin:20px auto !important;}
.signin-box h2:before {content:"Dive in";visibility:visible !important;}
.signin-box h2{visibility:hidden;}
.signin-box h2:after {visibility:visible !important;float:right;content:url("http://ssl.gstatic.com/accounts/ui/google-signin-flat.png")}
}
@-moz-document url-prefix("https://plus.google."), domain("plus.google.com") {
#gbx1 {padding-bottom:9px;}
#gbq, #gbu {top:25px !important;}
}
