css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('hackforums.net'){
    .logo{
        display:none !important;
    }
    .menu ul{
        background:none !important;
    }
    #container{
        background:rgba(10,10,10,0.9) !important;
        border:none !important;
    }
    .thead{
        background:-moz-linear-gradient(center top , rgba(100,0,0,.8), rgba(0,0,0,.8)) no-repeat scroll 0 0 transparent !important; !important;
    }
    .tfoot{
        background:-moz-linear-gradient(center top , rgba(100,0,0,.8), rgba(0,0,0,.8)) no-repeat scroll 0 0 transparent !important; !important;
    }
    .tcat{
        background:#ababab !important;
        color: -moz-linear-gradient(center top , rgba(100,0,0,.8), rgba(0,0,0,.8)) no-repeat scroll 0 0 transparent !important; !important;
    }
    .selected{
        color:#000000 !important;
    }
    .shadetabs li a{
        background-image: none !important;
        background:#434343 !important;
        border-radius: 0px 0px 0px 0px !important;
        box-shadow:0px 1px 0px 0px #B20000 inset !important;
        padding-left:15px !important;
        padding-right:15px !important;
        text-shadow:none !important;
        font-family:Tahoma, Geneva, sans-serif !important;
    }
    .bitButton{
        background-image: none !important;
        background:-moz-linear-gradient(center top , rgba(200,0,0,.8), rgba(75,0,0,.8)) no-repeat scroll 0 0 transparent !important; !important;
        border-radius: 0px 0px 0px 0px !important;
        box-shadow:0px 0px 0px!important;
        border: 1px solid #000000 !important;
        padding-left:10px !important;
        padding-right:10px !important;
        text-shadow:none !important;
        font-family:Tahoma, Geneva, sans-serif !important;
    }
    .active{
        display:none !important;
    }
    .menu ul{
        padding-top:5px !important;
        height: 35px !important;
    }
    #panel{
        border: 1px solid #B20000 !important;
    }
    .bottommenu{
        border: 1px solid #B20000 !important;
    }
    .tborder{
        background: #4E4E4E !important;
    }
    .pagination_first{
        border: 1px solid rgba(100,0,0,.8) !important;
    }
    .pagination_page{
        border: 1px solid rgba(100,0,0,.8) !important;   
    }
    .pagination_previous{
        border: 1px solid rgba(100,0,0,.8) !important;    
    }
    .pagination_current{
        border: 1px solid rgba(100,0,0,.8) !important;
    } 
    .quick_jump{
        //background:#B20000 !important;  
    } 
    .prefix{
        color:#B20000 !important;    
    }
    .pagination_last{
        border: 1px solid rgba(100,0,0,.8) !important;    
    }
    .pagination_next{
        border: 1px solid rgba(100,0,0,.8) !important;
    }
    .quick_jump
        background: url (http://img812.imageshack.us/img812/5142/79w1.png) !important;
        width: 13px;
        height: 13px;
        padding-left: 13px;
        vertical-align: middle;
        border: none;
        
        }
.thead,.tfoot,.shadetabs li a,.bitButton,.shadetabs li a.selected{
  	background: #a90329;
	background: -moz-linear-gradient(top,  #a90329 0%, #8f0222 44%, #6d0019 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#a90329), color-stop(44%,#8f0222), color-stop(100%,#6d0019));
	background: -webkit-linear-gradient(top,  #a90329 0%,#8f0222 44%,#6d0019 100%);
	background: -o-linear-gradient(top,  #a90329 0%,#8f0222 44%,#6d0019 100%);
	background: -ms-linear-gradient(top,  #a90329 0%,#8f0222 44%,#6d0019 100%);
	background: linear-gradient(to bottom,  #a90329 0%,#8f0222 44%,#6d0019 100%);
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#a90329', endColorstr='#6d0019',GradientType=0 );

}.tborder{
 	background:#261F21 !important; 
}.shadetabs li a.selected{
 	color:#eee !important; 
}.logo{
 	display:none !important; 
}#container .menu ul{
 	background:url(http://f.cl.ly/items/3a0Q2s0u192l1R281w2W/Untitled-1.png) !important; 
}#container{
 	border: 5px solid #630404 !important; 
}.tcat{
 	color: #000 !important; 
}.trow1 div,.bottommenu{
 	border:solid 1px #333 !important; 
}#panel{
  	border:solid 1px #700505 !important; 
}.pagination a { 
	border: 1px solid #4B4646;
}input[type='text'],input[type='password']{
 	background: #ffffff;
	background: -moz-linear-gradient(top,  #ffffff 0%, #cecece 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(100%,#cecece));
	background: -webkit-linear-gradient(top,  #ffffff 0%,#cecece 100%);
	background: -o-linear-gradient(top,  #ffffff 0%,#cecece 100%);
	background: -ms-linear-gradient(top,  #ffffff 0%,#cecece 100%);
	background: linear-gradient(to bottom,  #ffffff 0%,#cecece 100%);
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#ffffff', endColorstr='#cecece',GradientType=0 );
 
}
}
