css:
@-moz-document url-prefix("http://blog.livedoor.jp/news23vip/") {

  #header2, #blog-title, #sub, #extra, .pager,
  .blog-title-outer, #recent-entry, *[class*="ad-"],
  #comment-form, .article-footer, .kar_wraper, center,
  #imobile_adspotdiv1, .footer-outer, #trackbacks-wrapper
  { display: none !important; }

  div#main div.column-inner {
    width: 800px;
    margin: 2em auto;
  }
  h2.article-title {
    font-size: 24px;
    padding: 2em 1em;
    background-color: #fff;
  }
  h2.article-title a {
    text-decoration: none;
  }
  div#container {
    border: none;
    background-color: #f2f2f2;
  }
  div.article-outer, div#comments-wrapper {
    border: 1px solid #e6e6e6;
    box-shadow: 0 1px 3px #eee;
  }

}
