css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("feedly.com") {


.sideAreaModule, .discoverModule_part{
      display: none !important;
}


.narrow .panels {
      visibility: visible !important;
}


#feedlyTabs {
      opacity: 1 !important;
      overflow-y: auto !important;
}


.navSelector {
      line-height: 9px !important;
}



#navSelector_store{
      width: 80px !important;
}



#mainBar{
      margin-left: 280px !important; 
}



#feedlyTabs{
      margin-top: -20px !important;
      height:100% !important; 
      padding-left: 10px !important;
      background: rgb(240,240,240) !important;
}



#feedlyTabsHolder{ 
     height:100% !important; 
     width: 250px !important;}



.u100Entry .title {
      border-left:solid 7px #0080FF !important; 
      padding-left: 13px !important;
      font-size: 32px !important;
}


.metadata {
      border-left:solid 22px #ffffff !important;
}


.entryBody, .summary b, .entryBody .content b {
      text-align: justify !important;
      text-shadow: 0px 0px 1px rgba(0, 0, 0, 0.2)!important;
      color: #777777 !important;
      font-size: 18px !important;
      word-spacing: -1px !important;
      line-height: 1.3888em !important;
      font-family: "Lucida Grande","Lucida Sans     Unicode",Helvetica,Arial,Verdana,sans-serif!important;
}


BODY.home {
      background: #FFFFFF  !important; 
}





}
