css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newyorkpost.com") {

#hat-wrap, #rt-col, #footer-nav, #footer,
div.share-mini:nth-child(2),
#outbrain-block, #pagesix_mininav,
#sports_mininav, #entertainment_mininav,
#pagesix-nav > div:nth-child(2),
#sports-nav > div:nth-child(2),
.story-wrap, #news_mininav, #stories-menu, .story-page #page-wrap #container #lt-col-wrap #lt-col {
    display: none !important;
    }

#ct-col {
    background: none !important;
    }
#masthead-wrap {
    min-height: 65px !important;
    margin-top: -25px !important;
    }
#page-wrap {
    padding-top: 0 !important;
    }
#fat-header.open, #fat-header.open .nav-item {
    height: 50px !important;
    }
#lt-col-wrap {
    width: 960px !important;
    }
#ct-col,  #story {
    width: 760px !important;
    }
.homepage #ct-col, .story-page #ct-col {
    width: 710px !important;
    }

.homepage #lt-col, .homepage .story-block .block-links  {
    width: 220px !important;
    }
.homepage .story-block .story-list {
    width: 440px !important;
    }
.story-block .story-list-item {
    width: 210px !important;
    }
.story-page #lt-col-wrap {
    background: #fff !important;
    }
#story-body p {
    font-size: 1.8em !important;
    line-height: 1.2 !important;
    color: #6b6b6b !important;
}

.block p, .post-ten-item p {
    font-size: 1.3em !important;
    color: #6b6b6b !important;
    }
.block h4, .block h4 > a  {
    font-size: 1.2em !important;
    }

#story-body .caption, #story-body .photo_caption, #story-body .story_review {
    font: 1.1em arial !important;
    }
#story-body .photo-credit, #story-body .photo_credit {
    margin: 0 10px 5px 10px !important;
    }


}
