css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com"){


#sponsorsModule_part, #fusionModule_part, #mobileModule_part, #discoverModule_part ,.sideareamodule
{display:none !important;}

#mainarea, #feedlypart
{font-family: segoe ui  !important;}
}

.favicon
{display:none !important;}

.tab .feedIndex{
background: url(http://i.imgur.com/YKypjvC.png?1) no-repeat !important;
opacity:1 !important;}

.feedTitle {
font-family: segoe ui !important;
font-weight: bold !important;
font-size: 12px !important;
}
#feedlytitlebar {
font-family: calibri !important;
font-weight: bold  !important;
font-size: 28px !important;
}
.u100Entry .title, .title.read, .label.nonEmpt  {
font-family: calibri !important;
font-weight: bold  !important;
font-size: 15px !important;
}
.label.empty, .label, #who {
font-family: segoe ui !important;
font-size: 12px !important;
}
.home  .entryBody {
font-family: segoe ui !important;
font-size: 14px !important;
}
.feedIndexTitle, .navselector, .navselector.selected {
font-family: segoe ui !important;
font-weight: bold  !important;
font-size: 12px !important;
}
.feedunreadcount , .categoryunreadcount {
font-family: calibri !important;
font-weight: bold  !important;
font-size: 13px !important;
}
.action {
font-family: segoe ui !important;
font-size: 12px !important;
}
.entryTitle.title.unread {
font-size: 19px !important; 
}
.title.read {
font-size: 19px !important; 
}
.u100Frame.selectedEntry {
border-color: transparent !important;
box-shadow: 0 0 5px silver !important;
width: 122% !important; 
}
.u100Frame {
border-color: transparent !important;
box-shadow: 0 0 1px silver !important;
width: 122% !important; 
}
#sideArea { display: none !important; }
.label h2 { margin-top: 30px !important; }

#feedlyTabsHolder {
font-family: segoe ui !important;
box-shadow: 0 0 1px silver !important;
}
#feedlyPart0.area {
box-shadow: 0 0 1px silver !important;
margin-left: 1px !important; 
}
