css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reader.aol.com") {

.article-item-view .article-content {
    color: #656D73;
    direction: rtl;
    padding: 20px;
    text-align: right;
    width: 560px;
}
.article-item-list {
    direction: rtl;
    text-align: right;
}
h1, h2, h3, h4, h5, h6 {
    color: #2C3E50;
    font-family: "iranian sans",arial !important;
    text-rendering: optimizelegibility;
}
p {
    font-family: "iranian sans",arial !important;
    line-height: 18px;
    direction: rtl;
}
.article-date i, .article-tags i {
    float: right;
    font-size: 1.2em;
    margin-right: 10px;
    position: relative;
    top: 3px;
}
.feed-header h2, .article-header h2 {
    color: #2C3E50;
    float: right;
    font-size: 18px;
    line-height: 48px;
    margin: 0;
    overflow: hidden;
    padding: 0 10px 0 20px;
    text-overflow: ellipsis;
    white-space: nowrap;
    direction: rtl;
}
.article-container .btn-group-wrap, .article-item .btn-group-wrap, .organize-container .btn-group-wrap {
    float: left;
    font-size: 0;
}
.nav .nav-list {
    bottom: 20px;
    color: #6F7F8D;
    font-family: "iranian sans",arial !important;
    margin: 0;
    overflow-y: auto;
    padding: 10px 0 20px;
    position: absolute;
    top: 49px;
    width: 219px;
}
.nav-list a, .nav-list .subscription-item, .nav-list .category-item {
    color: #2C3E50;
    display: block;
   font-family: "iranian sans",arial !important;
    margin-right: 40px;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}
.article-item-view .article-content {
    color: #656D73;
    font-family: tahoma;
    padding: 20px;
    width: 560px;
}
.article-container-home-side {
    direction: rtl;
    float: right;
    font-family: "iranian sans",arial !important;
    text-align: right;
    width: 280px;
}

.article-item-home {
    border-bottom: 1px solid #E1E8ED;
    cursor: pointer;
    direction: rtl;
    height: 100px;
    overflow: hidden;
    padding: 10px;
    text-align: right;
}

.article-item-home .article-title {
    font-size: 18px;
    font-weight: 300;
    margin-bottom: 6px;
    padding-top: 15px;
}
.article-item-home .article-content {
    font-family: tahoma;
    font-size: 12px;
    line-height: 18px;
}


h2.article-title {
    font-size: 1.8em;
    font-weight: 300;
    line-height: 1.2em;
    padding-bottom: 10px;
    text-align: right;
    padding-top: 12px;
}



}
