css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^http://board\..*\.ogame\..*"){

    /* Made By Pouley For OGame, Edited by Ludofloria to fit the board */

    html{
    background: transparent !important;
    } 

    body{
    background-image: url(http://skins.aifesdespets.fr/forum/bkd.jpg) !important;
    background-repeat: no-repeat !important;
    background-color: black !important;
    background-attachment: fixed !important;
    min-width: 1450px !important;
    }

    div#headerContainer{
    background: url(http://skins.aifesdespets.fr/forum/bkd_top.png) top center no-repeat !important;
    }

    /*****************************
                   Shitty GF bar
    ******************************/

    #mmonetbar #mmoContent.mmonewsout,
    div#mmoGamesOverviewPanel{
    width: 580px !important;
    }

    #mmonetbar, #mmoLogo, .mmoBoxLeft, .mmoBoxMiddle, .mmoBoxRight, #mmoGamesOverviewToggle h4, .mmoNbBoxEdge{
    background-image: none !important;
    }

    #mmonetbar{
    background: url(http://skins.aifesdespets.fr/forum/bkd_barreGf.png) top center no-repeat !important;
    }

    #mmoGamesOverviewToggle h4 a, #mmonetbar .mmoSelectArea{
    border: 0px !important;
    }

    #mmonetbar .mmoSelectbox, #mmoGamesOverviewToggle h4 a{
    background-color: transparent !important;
    }

    #mmonetbar .mmoSelectArea .mmoSelectButton, 
    div#mmoGamesOverviewToggle h4 a span.mmoNbPseudoSelect_icon{
    background-image: url(https://skins.aifesdespets.fr/rebuild_d/netbar_sprites.png) !important;
    }

    #mmoNews{
    display: none !important;
    }

    #adsTop{
    display: none !important;
    }

    /**********************
        Hide the logo and replace the search container
    ***********************/

    #logo{
    width: 1px !important;
    }

    #search {
    top: 97px !important;
    left: auto !important;
    right: -66px !important;
    }

    #search input#searchInput{
    border: none !important;
    }

    #search .inputText{
    background-color: rgba(0,0,0,0) !important;
    width: 100px !important;
    }

    #search div.pageMenu{
    top: 1px !important;
    left: -11px !important;
    }

    #search div.pageMenu a{
    background-color: transparent !important;
    }

    /*********************
        User panel
    **********************/

    .userPanelInner {
    display: block !important;
    background: none !important;
    }

    #userAvatar {
    display: none !important;
    }

    #userNote{
    position: absolute !important;
    top: -8px !important;
    left: -610px !important;
    }

    #userMenu{
    display: inline !important;
    padding: 0 !important;
    position: relative !important;
    height: auto !important;
    top: 1px !important;
    left: auto !important;
    right: -80px !important;
    vertical-align: top !important;
    }

    #userMenu li{
    margin: 0 15px 0 0 !important;
    }

    #userMenu li a span{
    color: rgb(111, 159, 200) !important;
    }

    #userMenu li a:hover span{
    color: rgb(153, 204, 0) !important;
    text-decoration: underline !important;
    }

    #userMenu li a img{
    display: none !important;
    }

    /*******************
         Menu 
    ********************/

    #mainMenu{
    position: absolute !important;
    background: none !important;
    width: 173px !important;
    padding: 0 !important;
    left: 14% !important;
    height: 360px !important;
    background: url(https://skins.aifesdespets.fr/rebuild_d/bg_menu_new.png) no-repeat !important;
    }

    @media (max-width: 1880px) {
        #mainMenu{
            left: 5% !important;
        }
    }

    .mainMenu a{
    font: bold 10px/25px verdana,arial,helvetica,sans-serif !important;
    }

    .mainMenu ul{
    width: 173px !important;
    padding: 0 !important;
    }

    .mainMenu li,
    .mainMenu li a{
    text-align: left !important;
    background: none !important;
    width: 173px !important;
    height: 30px !important;
    padding: 0 !important;
    margin: 0 !important;
    }

    .mainMenu li{
    width: 173px !important;
    }

    .mainMenu li a{
    padding-left: 20px !important;
    }

    .mainMenu li.active,
    .mainMenu li.firstActive{
    background: url(https://skins.aifesdespets.fr/rebuild_d/bg_menu_new-active.png) no-repeat !important;
    color: #c0cedc !important;
    height: 30px !important;
    padding-left: 20px !important;
    }

    .mainMenu li.active a,
    .mainMenu li.firstActive a{
    line-height: 30px !important;
    padding-left: 20px !important;
    }

    .mainMenu li.last a,
    .mainMenu li.last a:hover{
    color: rgb(255,215,0) !important;
    }

    .mainMenu ul li a:hover{
    background: url(https://skins.aifesdespets.fr/rebuild_d/bg_menu_new-hover.png) no-repeat !important;
    text-decoration: none !important;
    color: #c0cedc !important;
    }

    .mainMenu li.active a:hover,
    .mainMenu li.firstActive a:hover{
    background: none !important;
    }

    /********************
       Info Panel 
    *********************/

    div.info{
    position: absolute !important;
    top: -200px !important;
    left: 220px !important;
    padding: 0 !important;
    }

    div#infopanel{
    background: none !important;
    width: auto !important;
    padding: 0 !important;
    }

    div#infopanel p{
    position: relative !important;
    top: 8px !important;
    left: 50px !important;
    }

    div.info center{
    position: absolute !important;
    display: block !important;
    top: 160px;
    left: 500px;
    width: 600px !important;
    }

    /*********************
        Info zone
    **********************/

    p.info{
        width: 610px !important;
        height: 115px !important;
        padding-top: 35px !important;
        padding-left: 30px !important;
        padding-right: 30px !important;
        padding-bottom: 0 !important;
        background: url(http://skins.aifesdespets.fr/forum/box_info.png) !important;
        border: none !important;
        position: relative !important;
        left: 15% !important;
    }

    .breadCrumbs {
    /* TODO : Change this picture */
        background: url(https://skins.aifesdespets.fr/rebuild_d/fleetStatus-bg.png) no-repeat !important;
        height: 28px !important;
        padding-top: 3px !important;
        padding-left: 15px !important;
    }

    .breadCrumbs img{
        display: none !important;
    }

    /*********************
        Portal
    **********************/

    #tplPortal .headlineMenu, 
    #tplPortalEdit .headlineMenu{
    position: relative !important;
    top: -45px !important;
    }

    #leftboxcontent h3,
    #leftboxes h3,
    #rightboxcontent h3,
    #rightboxes h3,
    #centerboxcontent h3,
    #centerboxes h3{
    margin-top: 4px !important;
    }

    #leftboxcontent,
    #leftboxes{
    position: relative !important;
    left: -20px !important;
    }

    #leftboxcontent .containerHead,
    #leftboxes .containerHead,
    #rightboxcontent .containerHead,
    #rightboxes .containerHead {
    width: 222px !important;
    height: 30px !important;
    margin: 0 !important;
    background: url(https://skins.aifesdespets.fr/rebuild_d/box_1_title.png) no-repeat scroll -5px 0px transparent !important;
    }

    #leftboxcontent .titleBarPanel .containerContent,
    #leftboxcontent .titleBarPanel .containerIcon,
    #rightboxcontent .titleBarPanel .containerContent,
    #rightboxcontent .titleBarPanel .containerIcon,
    #leftboxes .titleBarPanel .containerContent,
    #leftboxes .titleBarPanel .containerIcon,
    #rightboxes .titleBarPanel .containerContent,
    #rightboxes .titleBarPanel .containerIcon,
    #centerboxcontent .titleBarPanel .containerContent,
    #centerboxcontent .titleBarPanel .containerIcon,
    #centerboxes .titleBarPanel .containerContent,
    #centerboxes .titleBarPanel .containerIcon  {
    margin-top: 0px !important;
    margin-left: 0px !important;
    }

    #leftboxcontent .containerHead a img,
    #rightboxcontent .containerHead a img,
    #leftboxes .containerHead a img,
    #rightboxes .containerHead a img,
    #centerboxcontent .containerHead a img,
    #centerboxes .containerHead a img{
    visibility: hidden !important;
    }

    #leftboxcontent .containerHead a,
    #rightboxcontent .containerHead a,
    #leftboxes .containerHead a,
    #rightboxes .containerHead a {
    background: url(http://skins.aifesdespets.fr/forum/netbar_sprites.png) 0px -5px no-repeat !important;
    }

    #leftboxcontent .container-1,
    #rightboxcontent .container-1,
    #leftboxes .container-1,
    #rightboxes .container-1 {
    background: url(http://skins.aifesdespets.fr/forum/box_1_content.png) no-repeat scroll -5px 0px transparent !important;
    width: 222px !important;
    padding-left: 8px !important;
    min-height: 60px !important;
    }

    #leftboxcontent .titleBarPanel:after,
    #rightboxcontent .titleBarPanel:after,
    #leftboxes .titleBarPanel:after,
    #rightboxes .titleBarPanel:after{
    content: url(https://skins.aifesdespets.fr/rebuild_d/box_1_bottom.png) !important;
    margin-left: -5px !important;
    }

    div#buddies7.container-1.buddiesContainer .container-1,
    div#birthday1 .containerContent .container-1,
    div#birthday1 .containerContent .container-2{
    background: none !important;
    width: 170px !important;
    padding: 0 !important;
    min-height: 0px !important;
    }

    #leftboxcontent .container-1 div.containerContent,
    #leftboxes .container-1 div.containerContent{
    width: 170px !important;
    }

    #rightboxcontent #customBox110.container-1 form,
    #rightboxes #customBox110.container-1 table{
    padding-top: 15px !important;
    width: 190px !important;
    }

    #centerboxcontent,
    #centerboxes{
    margin-left: 10px !important;
    width: 568px !important;
    }

    #centerboxcontent div.messageContent img{
    max-width: 520px !important;
    }

    #centerboxcontent .containerHead,
    #centerboxes .containerHead {
    height: 27px !important;
    margin: 0 !important;
    background: url(http://skins.aifesdespets.fr/forum/box_2_title.png) no-repeat scroll -5px 0px transparent !important;
    }

    #centerboxcontent .containerHead a,
    #centerboxes .containerHead a{
    background: url(http://skins.aifesdespets.fr/forum/netbar_sprites.png) 0px -5px no-repeat !important;
    }

    #centerboxcontent div.container-1,
    #centerboxcontent div.container-2{
    background: none!important;
    }

    #centerboxcontent #news4 .content{
    background: url(http://skins.aifesdespets.fr/forum/box_2_content.png) no-repeat scroll -4px 0px transparent !important;
    }

    #centerboxcontent .titleBarPanel:after{
    content: url(http://skins.aifesdespets.fr/forum/box_2_bottom.png) !important;
    margin-left: -4px !important;
    }

    /*******************
     Body of the board
    ********************/

    #main{
    background: none !important;
    }

    #tplIndex .containerHead,
    #tplBoard .containerHead{
    background: url(http://skins.aifesdespets.fr/forum/sectionHead.png) no-repeat !important;
    height: 32px !important;
    padding: 0 !important;
    }

    #tplBoard .containerHead{
    margin-bottom: 8px !important;
    }

    #tplBoard .containerHead h3{
    position: relative !important;
    top: 7px !important;
    }

    #tplBoard #boardlist:before{
    content: url(http://skins.aifesdespets.fr/forum/sectionHead.png) !important;
    position: relative !important;
    bottom: -3px !important;
    }

    #tplBoard .mainHeadline{
    display: block !important;
    }

    #tplIndex .containerHead .containerContent,
    #tplBoard .containerHead .containerContent{
    margin-left: 10px !important;
    }

    #tplIndex .containerHead p{
    text-align: center !important;
    z-index: 5;
    }

    /* Click on the header -> compact the list */

    #tplIndex .containerHead .boardlistTitle .containerIcon,
    #tplBoard .containerHead .containerIcon{
    margin: 0 !important;
    }

    #tplIndex .containerHead .boardlistTitle .containerIcon img,
    #tplBoard .containerHead .containerIcon img{
    display: none !important;
    }

    #tplIndex .containerHead .boardlistTitle .containerIcon a,
    #tplBoard .containerHead .containerIcon a{
    margin: 0 !important;
    width: 958px !important;
    height: 32px !important;
    position: absolute !important;
    left: 0 !important;
    }

    #tplIndex .containerHead .boardlistTitle,
    #tplBoard .containerHead .boardlistTitle {
    margin-left: 0 !important;
    }

    #tplIndex .containerHead .boardlistTitle h3,
    #tplBoard .containerHead h3{
    text-align: center !important;
    }

    #tplIndex li ul,
    #tplBoard .tableList,
    #tplBoard #boardlist li .container-1,
    #tplBoard #boardlist li .container-2 {
    padding-top: 10px !important;
    background: url(http://skins.aifesdespets.fr/forum/sectionContent.png) repeat-y !important;
    margin-left: 1px !important;
    margin-bottom: 0px !important;
    }

    .border{
    margin-bottom: 0 !important;
    padding: 0 !important;
    }

    #tplBoard .content .container-1 {
    margin-left: 150px !important;
    }

    #tplBoard .content .container-1 p{
    text-align: center !important;
    padding-top: 5px !important;
    background: url(https://skins.aifesdespets.fr/rebuild_d/fleetStatus-bg.png) no-repeat !important;
    height: 32px !important;
    width: 658px !important;
    }

    #tplBoard .tableList{
    padding-left: 5px !important;
    }

    #tplIndex .boardlistSubboards ul,
    #tplBoard .tableList .tableHead a{
    background: none !important;
    border: none !important;
    }

    #tplIndex .container-1,
    #tplIndex .container-2,
    #tplBoard .container-1,
    #tplBoard .container-2{
        background: none !important;
        padding-left: 15px !important;
    }

    #tplIndex .boardlistLastPost {
    	width: 19% !important;
    }

    #tplIndex .category:after,
    #tplBoard #topThreadsStatus:after,
    #tplBoard #normalThreadsStatus:after,
    #tplBoard #boardlist:after{
        content: url(http://skins.aifesdespets.fr/forum/sectionFooter.png) !important;
        margin-left: 1px !important;;
    }

    #tplThread div .message div .messageContent .color-1,
    #tplThread div .message div .messageContent .color-2{
    	background: none !important;
    }

    #tplThread .quickReply .container-1{
    	background: url(http://skins.aifesdespets.fr/forum/sectionContent.png) !important;
    }

    /*******************
       GLOBAL THINGS
    ********************/


    .prefix{
    	color: #71b2ff;
    }

    div .message:before{
        content: url(http://skins.aifesdespets.fr/forum/sectionHead.png) !important;
        height: 32px !important;
        position: relative !important;
        bottom: -3px !important;
    }

    div .message:after{
        content: url(http://skins.aifesdespets.fr/forum/sectionFooter.png) !important;
        height: 32px !important;
        position: relative !important;
        left: 1px !important;
    }

    div .message > .container-1,
    div .message > .container-2,
    div .message > .container-3{
   		background: url(http://skins.aifesdespets.fr/forum/sectionContent.png) !important;
    }

    div#adsRight{
    	right: -210px !important;
    }

    /********************
      Buttons
    *********************/

    .contentHeader,
    .contentFooter{
   		width: auto !important;
    }

    .smallButtons img{
    	width: 16px !important;
    }

    .smallButtons a, 
    li.extraButtons a,
    .largeButtons li a,
    .messageNumber{
        background-image: none !important;
        background: linear-gradient(to bottom, rgba(150,150,150, 0.35) 0px, rgba(255,255,255,0.5) 30%, rgba(150,150,150,0.5) 31%, rgba(100, 100, 100, 0.35) 100%) repeat scroll 0% 0% transparent !important;
        border: none !important;
        text-shadow: 2px 2px 2px rgba( 0, 0, 0, 1) !important;
        border-radius: 2px !important;
    }

    .smallButtons a:hover,
    li.extraButtons a:hover,
    .largeButtons li a:hover,
    .messageNumber:hover{
        background: linear-gradient(to bottom, rgba(200,200,200, 0.35) 0px, rgba(255,255,255,0.5) 30%, rgba(200,200,200,0.5) 31%, rgba(150, 150, 150, 0.35) 100%) repeat scroll 0% 0% transparent !important;
        text-decoration: none !important;
    }

    /* Footer */

    .copyright{
        background: url(http://skins.aifesdespets.fr/forum/sitefooter.png) no-repeat 4px 0px !important;
        width: 780px;
        padding-top: 7px !important;
        position: relative!important;
        left: 10% !important;
    }

    .copyright:after{
        color: #71b2ff;
        content: " - Skin by Poulet_Nucleaire & Ludofloria ©";
    }

}
