@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cafepress.com") {  

body {
color:#606060!important;
background:white!important;
}

.login-social {
    display: none;
}

.promo-modal {
    display:none;
}

table th {
    color: #3366CC;
    cursor: pointer;
    font-size: 0px !important;
    text-decoration: underline;
}

#mainContent {
    margin: 15px;
}

.form-container .login-fb {
    display:none!important;
}

.form-container .login-google {
    display: none!important;
}

.login-divider {
    display: none!important;
}

.login-divider span {
    display: none!important;
}

.nav, .nav-tab {
    display: none;
    float: left;
}

#tippyTopContainer {
display:none!important;
}

#monetate_selectorBanner_3d64fd79_00 {
display:none!important;
}

.autoPadDiv {
color:white!important;
}

#report_panel {
    color: #606060!important;
    font-family: Arial,sans-serif;
    font-size: 14px;
}

.button {
    -moz-transition: all 0.1s ease 0s;
    background: none repeat scroll 0 0 #79A618;
    border: medium none;
    color: #FFFFFF;
    cursor: pointer;
    font-size: 14px;
    line-height: 27px;
    margin: 0;
    padding: 2px 10px;
    text-align: center;
}



.feed-info .button {
    float: left;
    font-family: 'Roboto Condensed',sans-serif;
    margin-top: 20px;
    margin-bottom:5px!important;
color:white!important;
}

#signin-menu-signin a {
    background: none repeat scroll 0 0 #79A618;
    box-shadow: 1px 1px 4px #DDDDDD;
    color: white!important;
    display: block;
    margin-bottom: 5px;
    text-align: center;
    width: 100px;
}



.no-activity .button {
    display: inline-block;
    padding: 5px 10px;
    color:white!important;
}

a:link, a:visited {
    color:#3366CC!important;
    font-weight:bold!important;
    
}

a:hover {
 color:maroon!important;
 font-weight:bold!important;
 text-decoration:none!important;
 }


.members-menu-item a, .members-menu-item a:hover {
    color: #FFFFFF!important;
    display: block;
    font-size: 14px;
    line-height: 33px;
    padding: 0 10px;
    text-decoration: none;
font-weight:bold!important;
}


#TB_window a.cpButton:link, #TB_window a.cpButton:visited, a.cpButton:link, a.cpButton:visited, #TB_window .cpButtonJs a.cpButtonAnchor:link, #TB_window .cpButtonJs a.cpButtonAnchor:visited, .cpButtonJs a.cpButtonAnchor:link, .cpButtonJs a.cpButtonAnchor:visited {
    color: #FFFFFF!important;
    text-decoration: none;
}


.container960 {
    font-size: 1.2em;
    margin: 0 auto;
    text-align: left;
background:white!important;
}

.breadCrumbIndicator {
font-weight:bold!important;
}

div {
font-weight:bold!important;
}


.welcome {
    background: none repeat scroll 0 0 silver;
    color: #FFFFFF;
    display: none;
    line-height: 20px;
    padding: 20px 40px;
    position: relative;
}
.nav-search {
    margin: auto;
    position: relative;
   
}

#global-header-searchform {
    background: none repeat scroll 0 0 #FFFFFF;
    color: #888888;
    float: right;
    padding: 0 0 0 10px;
    position: relative;
    width: auto;
}
}
