css:
@-moz-document domain("www.carnageblender.com") {
body {
	width: 739px;
	margin: 0 0 0 10px;
	background-color: #4c4c4c !important;
	font-size: 13px;
	color: black;
	font-family: Verdana, sans-serif;
}

p { 
  margin-top: 1em;
  margin-bottom: 1em;
}

ul { 
  clear: both;
}

form { 
  	margin: 0;
}

input, textarea {
	font-size: 12px;
	background-color: #666666 !important;
	border: 2px solid #222 !important;
	border-bottom: 2px solid #222 !important;
	border-right: 2px solid #222 !important;
	margin: 3px;
	color: #000000 !important;
}

select {
	margin: 2px;
	background-color: #dddddd !important;
  	font-size: x-small; 
  	font-family: Tahoma;
  	border: 1px solid #4C2022;
}

#context-bar { 
  	width: 100%; 
  	margin-bottom: 5px;
}

.context-font {
  	font-size: 11px;
  	white-space: nowrap;
}





.winner-font {font-size:9pt;font-family:tahoma;color:#00CD00 !important;}
.loser-font {font-size:9pt;font-family:tahoma;color:#ff00ff !important;}
.tie-font {font-size:9pt;font-family:tahoma;color:black !important;}

.med-font {
  font-size:12px;
}

.white-font {
	font-size:10pt; 
	font-family: Tahoma; 
	color: white; 
	text-align: left;
}

.white-bg {
	background-color: #4c4c4c !important;
}

.fine-print {
  font-size: small; 
  font-family: Tahoma; 
  color: black; 
}

#flash .fine-print {
    color: white;
}

.small-font{
  font-size: xx-small; 
  font-family: Tahoma;
}





a {
	color: #000000 !important;
}

a:visited {
	color: #dddddd !important; 
}

a:hover {
	color: #ffffff !important; 
}

a:active {
	color: #ffffff !important; 
}







.buttons {
    margin-top: 10px;
    margin-bottom: 10px;
}

a.button {
    background: transparent url('http://i.imgur.com/e7GhMwk.png?1') no-repeat scroll top right !important;
    color: #eeeeee;
    display: block;
    float: left;
    font: normal 12px arial, sans-serif;
    height: 20px;
    margin-right: 10px;
    padding-right: 5px;
    margin-top: 0px;
    margin-bottom: 0px;
    text-decoration: none;
}
a.button span {
    background: transparent url('http://i.imgur.com/e7GhMwk.png?1') no-repeat top left !important;
    display: block;
    line-height: 14px;
	padding: 3px 4px 3px 9px;
    color: #ffffff !important;
}

a.button span:hover {
    color: #bbbbbb!important;
}

a.button:active {
    background-position: bottom right;
    outline: none;
}
a.button:active span {
    background-position: bottom left;
}







table {
  	border-collapse: collapse;
}

td,th { 
  	padding-left: 0.5em;
  	padding-right: 0.5em;
}


th {
  	vertical-align: top;
  	background: url('http://i.imgur.com/KRKr7bD.png?1') repeat-x !important;
  	background-color: #bbbbbb;
  	
}

th a {
	text-decoration: none;
}

tr:hover {
  	background-color: #4c4c4c !important;
}





h1, caption {
	clear: both;
	margin: 10px 0 2px;
	padding: 4px 0 3px 10px;
	background: url('http://i.imgur.com/QzE3WEm.png?1') repeat-x !important;
	height: 20px;
	color: white;
	font-weight: bold;
	font-size: 12px;
}

h1 a, h1 a:visited, caption a {
	color: white;
}

h1 a:hover {
	color: #CD1515 !important; 
}

h1 a:active {
	color: #551818 !important; 
}

h2 {
	margin-top: 10px;
	margin-bottom: 2px;
	font-size: 14px;
	font-weight: bold;
	clear: both;
}





#flash {
	margin: 10px;
	padding: 10px;
    background-color:#000000 !important; 
    color:#ffffff !important;
	font-size:12pt; 
	font-weight:bold;
}

.tutorial { 
    padding: 5px;
    background-color:#000000 !important; 
    color:#ffffff !important; 
    font-size:12pt; 
    font-weight:bold;
}

.gamestats { 
  float:left;
  margin-right: 1%;
}
.center {
  text-align: center;
  margin: auto;
}

.stats {
	background-color: #4c4c4c !important;
	width: 40%;
	float: left;
	padding-top: 5px;
	padding-bottom: 5px;
}

.raw { 
	font-size: 9px;
}

.section-bg {
	background-color: #666666 !important;
}

.block  {
	background-color: #666666 !important;
}

.boxContent li {
	background-color: #4c4c4c !important;
}

.tabs-container  {
	background-color: #666666 !important;
}

.tabs-nav  li{
  background-color: #666666 !important;
}


#equip select {
    font-size: xx-small;
}

#unequipped { 
	width: 80%;
}

.equipment {
	font-size: 11px;
	background-color: #4c4c4c !important;
	width: 57%;
	float: right;
	padding: 0.5em;
}


#train .vspace {
	height: 10px;
}

#train #misc {
	clear: both;
}

#train #misc a {
	margin-right: 10px;
}

#train #minions {
}

#train #stats {
	margin-top: 5px;
}

#train #statstable {
	width: 100%;
}







.thread-title {
    font-weight: bold;
}

.threads { 
    width: 100%;
}
.threads td { 
    padding-top: 2px;
    padding-bottom: 5px;
}

.post {
    display: inline-block;
    margin: 0px;
    width: 719px;
    padding: 15px 10px 15px 10px;
}

.post-bg {
    background-color: #666666 !important;
    border-radius: 12px;
	margin: 3px 0 3px 0;
	padding: 10px 8px;
	width: 722px;
	border-bottom: 2px solid black !important;
	border-color: #dddddd !important;
}

.post h2 {
    clear: none;
    margin: 0px 0px 5px 0px;
    font-size: 13px;
    width: 600px;
    float: right;
    border-bottom: 1px solid;
    padding: 0px 0px 3px 0px;
}

.post-body {
    width: 600px;
    float: right;
    margin: 0px;
    padding: 0px;
}

.post-thumb {
    margin-right: 10px;
    padding: 0px;
    display: inline;
    width: 100px;
    float: left;
    text-align: center;
    border-radius: 8px;
}
}

@-moz-document url-prefix("http://www.carnageblender.com/sidebar.tcl") {
#logo img {
     display: none;
}

#logo {
     width: 250px;
     height: 24px;
     background: url("http://i.imgur.com/j9fLNHz.jpg?1");
}

#logo td {
     background: none;
}

body { 
  width: 100%;
  background-color: #666666 !important;
  margin: 0;
}

fieldset { 
  display: block;
  width: 226px;
  border: 2px solid black !important;
  padding: 2px 5px 5px 5px;
  margin: 5px 0px 5px 5px;
  border-radius: 6px;
  border-top-left-radius: 0;
  background-color: #4c4c4c !important;
}

legend { 
  font-weight: bold;
}

td { 
  padding: 0;
}



a {
  text-decoration: none;
  color: #000000 !important;
}
a:hover {
  text-decoration: underline;
  color: #ffffff !important;
}
a:visited {
  color: #000000 !important; 
}

ul {
  margin: 0;
  padding: 0;
}

li { 
  list-style-type: none;
}





#help { 
  margin-left: 0px;
}

#help li { 
  margin-top: 2px;
  margin-left: 0.5em;
  display: inline;
}

#charlinks li { 
  margin-right: 0.5em;
  display: inline;
}

#ba { 
  margin-right: 0.5em;
}

#opponents { 
  position: relative;
}

#fightlinks li { 
  margin-right: 0.5em;
  display: inline;
}

#listbuttons li { 
  margin-right: 0.5em;
  display: inline;
}

#quicklinks div { 
  float: left;
  margin-right: 1em;
  font-size: 11px;
}
}

@-moz-document url-prefix("http://www.carnageblender.com/fight.tcl"), url-prefix("http://www.carnageblender.com/opponents-favorites.tcl"), url-prefix("http://www.carnageblender.com/party-manage.tcl"), url-prefix("http://www.carnageblender.com/blackmarket.tcl"), url-prefix("http://www.carnageblender.com/directory"), url-prefix("http://www.carnageblender.com/clans/"), url-prefix("http://www.carnageblender.com/stats/"), url-prefix("http://www.carnageblender.com/poll/"), url-prefix("http://www.carnageblender.com/forge") {
h2{font-size:14pt;}

form { 
  margin: 0;
}
option {
        background-color: #4c4c4c !important;
}
A:link {
	COLOR: #000000 !important; 
}
A:visited {
	COLOR: #dddddd !important; 
}
A:hover {
	COLOR: #ffffff !important; 
}
A:active {
	COLOR: #ffffff !important; 
}

BODY {
	SCROLLBAR-FACE-COLOR: #690000;
  	FONT-SIZE: 9pt;
  	SCROLLBAR-HIGHLIGHT-COLOR: #C51E1E;
  	SCROLLBAR-SHADOW-COLOR: black;
  	COLOR: #000000 !important;
  	SCROLLBAR-3DLIGHT-COLOR: #AE1313;
  	SCROLLBAR-ARROW-COLOR: #cccccc;
  	SCROLLBAR-TRACK-COLOR: #791212;
  	FONT-FAMILY: tahoma;
  	SCROLLBAR-DARKSHADOW-COLOR: black;
  	background-color:#4c4c4c !important;
  	margin:10px;
    width: 100%;
  	cellspacing: 0px;
  	cellpadding: 0px;
}
TD {
	FONT-SIZE: 9pt; FONT-FAMILY: tahoma;
}
TEXTAREA {
	FONT-SIZE: 9pt; background-color: #666666 !important; FONT-FAMILY: tahoma
}
INPUT {
	FONT-SIZE: 8pt; background-color: #666666 !important; FONT-FAMILY: tahoma
}
SELECT {
	FONT-SIZE: 8pt; background-color: #d5d5d5 !important; FONT-FAMILY: tahoma
}
FORM {
    MARGIN-BOTTOM: 0px
}

ol li font {
	COLOR: black; TEXT-DECORATION: none
}


.font {font-size:9pt; font-family:tahoma; color:black;} 

.white-font {font-size:10pt; font-family:tahoma; color:white; text-align: left;}


.small-font {font-size:xx-small; font-family:tahoma; color:black; font-weight:normal;}
