    padding: 5px;
    background-color:#000000 !important; 
    color:#ffffff !important; 
    font-size:12pt; 
    font-weight:bold;
}

#flash {
  margin: 10px;
  background-color:#000000 !important; 
  background-color:#ffffff !important;
  font-size:12pt; 
  font-weight:bold;
}

.padded td,th { 
  padding-left: 0.5em;
  padding-right: 0.5em;
}

dt {
  margin-top: 6px;
}
}

